&lt;h2 id=&#34;一-二分图匹配&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#一-二分图匹配&#34;&gt;#&lt;/a&gt; 一、二分图匹配&lt;/h2&gt;
&lt;p&gt;二分图是一种特殊的图，图中每一条边所关联的两个节点都属于两个不同的点集，匹配的定义是在二分图的一个子图中任意一条边都不依附于同一个节点，称为是一个匹配，最大匹配是所有匹配中边数最多的匹配。&lt;/p&gt;
&lt;p&gt;&lt;span class=&#34;exturl&#34; data-url=&#34;aHR0cHM6Ly93d3cubHVvZ3UuY29tLmNuL3Byb2JsZW0vUDMzODY=&#34;&gt;例题：二分图匹配&lt;/span&gt;&lt;/p&gt;
&lt;p&gt;Code:&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;#include &amp;lt;bits/stdc++.h&amp;gt;
using namespace std;
const int N=1001;

int n,m,E,rgh[N];
bool use[N],link[N][N];

bool find(int x)
&amp;#123;
	for(int i=1;i&amp;lt;=m;i++)
	&amp;#123;
		if(link[x][i] &amp;amp;&amp;amp; !use[i])
		&amp;#123;
			use[i]=true;
			if(!rgh[i] || find(rgh[i]))
			&amp;#123;
				rgh[i]=x;
				return true;
			&amp;#125;
		&amp;#125;
	&amp;#125;
	return false;
&amp;#125;

int main()
&amp;#123;
	scanf(&amp;quot;%d %d %d&amp;quot;,&amp;amp;n,&amp;amp;m,&amp;amp;E);
	for(int i=1;i&amp;lt;=E;i++)
	&amp;#123;
		int zuo,you;
		scanf(&amp;quot;%d %d&amp;quot;,&amp;amp;zuo,&amp;amp;you);
		if(zuo&amp;gt;n || you&amp;gt;m)
			continue;
		link[zuo][you]=true;
	&amp;#125;
	int sum=0;
	for(int i=1;i&amp;lt;=n;i++)
	&amp;#123;
		memset(use,0,sizeof(use));
		if(find(i))
			sum++;
	&amp;#125;
	printf(&amp;quot;%d\n&amp;quot;,sum);
	return 0;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
&lt;h2 id=&#34;二-最大流&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#二-最大流&#34;&gt;#&lt;/a&gt; 二、最大流&lt;/h2&gt;
&lt;p&gt;给定一个图，图中每一条边都有一个权值，把每一条边当做是水管，权值是就是最大流量，最大流问题，就是满足这些性质的情况下，源点到汇点的最大流量&lt;/p&gt;
&lt;p&gt;&lt;span class=&#34;exturl&#34; data-url=&#34;aHR0cHM6Ly93d3cubHVvZ3UuY29tLmNuL3Byb2JsZW0vUDMzNzY=&#34;&gt;例题：网络最大流&lt;/span&gt;&lt;/p&gt;
&lt;p&gt;Code:&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;#include &amp;lt;bits/stdc++.h&amp;gt;
using namespace std;
const int N=100001;
struct node
&amp;#123;
	int to,len,id;
	node(int tt,int ll,int ii)
	&amp;#123;
		to=tt,len=ll,id=ii;
	&amp;#125;
	node()
	&amp;#123;
	&amp;#125;
&amp;#125;;
vector &amp;lt;node &amp;gt; g[N];
vector &amp;lt;node &amp;gt; v[N];
int n,m,st,ed,pre[200001][2];
bool vis[N],flag[N];
queue &amp;lt;int &amp;gt; q;
bool bfs()
&amp;#123;
	q.push(st),vis[st]=true;
	while(!q.empty())
	&amp;#123;
		int t=q.front();
		q.pop();
		int size=g[t].size();
		for(int i=0;i&amp;lt;size;i++)
		&amp;#123;
			int to=g[t][i].to;
			int len=g[t][i].len;
			if(!vis[to] &amp;amp;&amp;amp; len&amp;gt;0)
			&amp;#123;
				vis[to]=true;
				pre[to][0]=t,pre[to][1]=len;
				if(to==ed)
					return true;
				q.push(to);
			&amp;#125;
		&amp;#125;
	&amp;#125;
	return false;
&amp;#125;
void dfs(int cur,int &amp;amp;val)
&amp;#123;
	int size=v[cur].size();
	for(int i=0;i&amp;lt;size;i++)
	&amp;#123;
		if(v[cur][i].to==pre[cur][0])
		&amp;#123;
			flag[v[cur][i].id]=true;
			val=min(pre[cur][1],val);
			dfs(v[cur][i].to,val);
		&amp;#125;
	&amp;#125;
	return;
&amp;#125;
int ek()
&amp;#123;
	int ans=0;
	while(bfs())
	&amp;#123;
		int minn=0x7f7f7f7f;
		dfs(ed,minn);
		for(int i=1;i&amp;lt;=n;i++)
		&amp;#123;
			int s1=g[i].size();
			int s2=v[i].size();
			for(int j=0;j&amp;lt;s1;j++)
				if(flag[g[i][j].id])
					g[i][j].len-=minn;
			for(int j=0;j&amp;lt;s2;j++)
				if(flag[v[i][j].id])
					v[i][j].len+=minn;
		&amp;#125;
		ans+=minn;
		memset(vis,0,sizeof(vis));
		memset(flag,0,sizeof(flag));
		memset(pre,0,sizeof(pre));
		while(!q.empty()) q.pop();
	&amp;#125;
	return ans;
&amp;#125;
int main()
&amp;#123;
	scanf(&amp;quot;%d %d %d %d&amp;quot;,&amp;amp;n,&amp;amp;m,&amp;amp;st,&amp;amp;ed);
	for(int i=1;i&amp;lt;=m;i++)
	&amp;#123;
		int a,b,c;
		scanf(&amp;quot;%d %d %d&amp;quot;,&amp;amp;a,&amp;amp;b,&amp;amp;c);
		g[a].push_back(node(b,c,i));
		v[b].push_back(node(a,0,i));
	&amp;#125;
	printf(&amp;quot;%d\n&amp;quot;,ek());
	return 0;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;另一种写法：&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;#include &amp;lt;bits/stdc++.h&amp;gt;
using namespace std;
const int N=100001;
const int INF=0x7f7f7f7f;
struct node
&amp;#123;
	int to;
	int cap;
	int flink;
	node(int tt,int cc,int ff)
	&amp;#123;
		to=tt,cap=cc,flink=ff;
	&amp;#125;
	node()
	&amp;#123;
	&amp;#125;
&amp;#125;;
vector &amp;lt;node &amp;gt; g[N];
bool vis[N];
int n,m,st,ed;
int dfs(int now,int aim,int val)
&amp;#123;
	if(now==aim)
		return val;
	vis[now]=true;
	int size=g[now].size();
	for(int i=0;i&amp;lt;size;i++)
	&amp;#123;
		int to=g[now][i].to;
		int len=g[now][i].cap;
		int f=g[now][i].flink;
		if(!vis[to] &amp;amp;&amp;amp; len&amp;gt;0)
		&amp;#123;
			int v=dfs(to,aim,min(len,val));
			if(v&amp;gt;0)
			&amp;#123;
				g[now][i].cap-=v;
				g[to][f].cap+=v;
				return v;
			&amp;#125;
		&amp;#125;
	&amp;#125;
	return 0;
&amp;#125;
int EK()
&amp;#123;
	int ans=0;
	while(1)
	&amp;#123;
		memset(vis,0,sizeof(vis));
		int flow=dfs(st,ed,INF);
		if(!flow)
			return ans;
		ans+=flow;
	&amp;#125;
	return ans;
&amp;#125;
int main()
&amp;#123;
	scanf(&amp;quot;%d %d %d %d&amp;quot;,&amp;amp;m,&amp;amp;n,&amp;amp;st,&amp;amp;ed);
	for(int i=0;i&amp;lt;m;i++)
	&amp;#123;
		int a,b,c;
		scanf(&amp;quot;%d %d %d&amp;quot;,&amp;amp;a,&amp;amp;b,&amp;amp;c);
		g[a].push_back(node(b,c,g[b].size()));
		g[b].push_back(node(a,0,g[a].size()-1));
	&amp;#125;
	printf(&amp;quot;%d\n&amp;quot;,EK());
	return 0;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
&lt;h2 id=&#34;三-最小费用最大流&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#三-最小费用最大流&#34;&gt;#&lt;/a&gt; 三、最小费用最大流&lt;/h2&gt;
&lt;p&gt;这部分用的是增广路算法，代码如下：&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;#include &amp;lt;bits/stdc++.h&amp;gt;
using namespace std;
const int N=100001;
const int INF=0x7f7f7f7f;
struct node
&amp;#123;
	int to;
	int len;
	int flink;
	int cost;
	node(int tt,int ll,int ff,int oo)
	&amp;#123;
		to=tt,len=ll,flink=ff,cost=oo;
	&amp;#125;
	node()
	&amp;#123;
	&amp;#125;
&amp;#125;;
vector &amp;lt;node &amp;gt; g[N];
bool flag[N];
int n,m,S,T,dis[N],flow[N],pre[N][2];
bool spfa()
&amp;#123;
	memset(pre,0,sizeof(pre));
	memset(dis,0x3f,sizeof(dis));
	memset(flow,0x3f,sizeof(flow));
	memset(flag,0,sizeof(flag));
	queue &amp;lt;int &amp;gt; q;
	q.push(S),dis[S]=0,pre[T][0]=-1,flag[S]=true;
	while(!q.empty())
	&amp;#123;
		int t=q.front();
		q.pop();
		flag[t]=false;
		int size=g[t].size();
		for(int i=0;i&amp;lt;size;i++)
		&amp;#123;
			int to=g[t][i].to;
			int len=g[t][i].len;
			int cos=g[t][i].cost;
			if(len&amp;gt;0 &amp;amp;&amp;amp; dis[to]&amp;gt;dis[t]+cos)
			&amp;#123;
				dis[to]=dis[t]+cos;
				flow[to]=min(flow[t],len);
				pre[to][0]=t;
				pre[to][1]=i;
				if(!flag[to])
					q.push(to),flag[to]=true;
			&amp;#125;
		&amp;#125;
	&amp;#125;
	return dis[T]!=0x3f3f3f3f;
&amp;#125;
int maxflow,mincost;
void MDMF()
&amp;#123;
	while(spfa())
	&amp;#123;
		int now=T;
		maxflow+=flow[T];
		mincost+=flow[T]*dis[T];
		while(now!=S)
		&amp;#123;
			g[pre[now][0]][pre[now][1]].len-=flow[T];
			g[now][g[pre[now][0]][pre[now][1]].flink].len+=flow[T];
			now=pre[now][0];
		&amp;#125;
	&amp;#125;
	return;
&amp;#125;
void add(int now,int to,int len,int cos)
&amp;#123;
	g[now].push_back(node(to,len,g[to].size(),cos));
	g[to].push_back(node(now,0,g[now].size()-1,-cos));
	return;
&amp;#125;
int main()
&amp;#123;
	scanf(&amp;quot;%d %d %d %d&amp;quot;,&amp;amp;n,&amp;amp;m,&amp;amp;S,&amp;amp;T);
	for(int i=0;i&amp;lt;m;i++)
	&amp;#123;
		int a,b,c,d;
		scanf(&amp;quot;%d %d %d %d&amp;quot;,&amp;amp;a,&amp;amp;b,&amp;amp;c,&amp;amp;d);
		add(a,b,c,d);
	&amp;#125;
	MDMF();
	printf(&amp;quot;%d %d\n&amp;quot;,maxflow,mincost);
	return 0;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;例题如下：&lt;/p&gt;
&lt;p&gt;&lt;span class=&#34;exturl&#34; data-url=&#34;aHR0cHM6Ly93d3cubHVvZ3UuY29tLmNuL3Byb2JsZW0vbGlzdD9rZXl3b3JkPSVFNiU5QyU4MCVFNSVCMCU4RiVFOCVCNCVCOSVFNyU5NCVBOCVFNiU5QyU4MCVFNSVBNCVBNyVFNSU4QSU5QiZhbXA7cGFnZT0x&#34;&gt;最小费用最大流&lt;/span&gt;&lt;/p&gt;
&lt;p&gt;两种算法的具体解析请移步另一篇文章&lt;/p&gt;

 &lt;h1 id=&#34;快读-快写和对拍的模板&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#快读-快写和对拍的模板&#34;&gt;#&lt;/a&gt; 快读、快写和对拍的模板&lt;/h1&gt;
&lt;p&gt;&lt;span id=&#34;more&#34;&gt;&lt;/span&gt;&lt;/p&gt;
&lt;h2 id=&#34;一-用途介绍&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#一-用途介绍&#34;&gt;#&lt;/a&gt; 一、用途介绍&lt;/h2&gt;
&lt;p&gt;其实我觉得不需要介绍太多，写这东西不就为了卡个常数吗，不卡常数用&lt;span class=&#34;katex&#34;&gt;&lt;span class=&#34;katex-mathml&#34;&gt;&lt;math xmlns=&#34;http://www.w3.org/1998/Math/MathML&#34;&gt;&lt;semantics&gt;&lt;mrow&gt;&lt;mi&gt;s&lt;/mi&gt;&lt;mi&gt;c&lt;/mi&gt;&lt;mi&gt;a&lt;/mi&gt;&lt;mi&gt;n&lt;/mi&gt;&lt;mi&gt;f&lt;/mi&gt;&lt;/mrow&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;scanf&lt;/annotation&gt;&lt;/semantics&gt;&lt;/math&gt;&lt;/span&gt;&lt;span class=&#34;katex-html&#34; aria-hidden=&#34;true&#34;&gt;&lt;span class=&#34;base&#34;&gt;&lt;span class=&#34;strut&#34; style=&#34;height:0.8888799999999999em;vertical-align:-0.19444em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;s&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;c&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;a&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;n&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.10764em;&#34;&gt;f&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;，&lt;span class=&#34;katex&#34;&gt;&lt;span class=&#34;katex-mathml&#34;&gt;&lt;math xmlns=&#34;http://www.w3.org/1998/Math/MathML&#34;&gt;&lt;semantics&gt;&lt;mrow&gt;&lt;mi&gt;c&lt;/mi&gt;&lt;mi&gt;i&lt;/mi&gt;&lt;mi&gt;n&lt;/mi&gt;&lt;/mrow&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;cin&lt;/annotation&gt;&lt;/semantics&gt;&lt;/math&gt;&lt;/span&gt;&lt;span class=&#34;katex-html&#34; aria-hidden=&#34;true&#34;&gt;&lt;span class=&#34;base&#34;&gt;&lt;span class=&#34;strut&#34; style=&#34;height:0.65952em;vertical-align:0em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;c&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;i&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; 不香吗&lt;/p&gt;
&lt;h2 id=&#34;二-代码&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#二-代码&#34;&gt;#&lt;/a&gt; 二、代码&lt;/h2&gt;
&lt;p&gt;Code:&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;inline void read(int &amp;amp; a)
&amp;#123;
    char ch;
    int fh=1;
    while(!(isdigit(ch)))
    &amp;#123;
        if(ch==&#39;-&#39;)
            fh=-1;
        ch=getchar();
    &amp;#125;
    a=ch-&#39;0&#39;;
    ch=getchar();
    while(isdigit(ch))
        a=(a&amp;lt;&amp;lt;3)+(a&amp;lt;&amp;lt;1)+ch-&#39;0&#39;,ch=getchar();
    a*=fh;
&amp;#125;
inline void write(int x)
&amp;#123;
    if(x&amp;lt;0)
	&amp;#123;//判断负数
        x=-x;
        putchar(&#39;-&#39;);
    &amp;#125;
    if(x&amp;gt;9) write(x/10);//位值拆数，递归
    putchar(x%10+&#39;0&#39;);//按位输出
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;对拍的 Code:&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;#include &amp;lt;windows.h&amp;gt;
#include &amp;lt;bits/stdc++.h&amp;gt;
using namespace std;
int main()
&amp;#123;
    int T=1000;
    while(T--)
    &amp;#123;
        system(&amp;quot;data.exe &amp;gt;  1.in&amp;quot;);
        system(&amp;quot;my.exe &amp;lt;1.in &amp;gt; 1.out&amp;quot;);
        system(&amp;quot;bc.exe &amp;lt;1.in &amp;gt; 2.out&amp;quot;);
        if(system(&amp;quot;fc 1.out 2.out&amp;quot;))
            break;
    &amp;#125;
    system(&amp;quot;pause&amp;quot;);
    return 0;
&amp;#125;
/*
data为数据生成器
my为自己的高效代码
bc为自己的暴力代码 记得每个文件点一下编译运行
如果错误，数据在1.in中
*/
&lt;/code&gt;&lt;/pre&gt;

 &lt;h1 id=&#34;高精度模板&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#高精度模板&#34;&gt;#&lt;/a&gt; 高精度模板&lt;/h1&gt;
&lt;p&gt;&lt;span id=&#34;more&#34;&gt;&lt;/span&gt;&lt;/p&gt;
&lt;h2 id=&#34;一-高精度简介&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#一-高精度简介&#34;&gt;#&lt;/a&gt; 一、高精度简介&lt;/h2&gt;
&lt;p&gt;什么时候用高精度呢，很简单，当你的答案大到练&lt;span class=&#34;katex&#34;&gt;&lt;span class=&#34;katex-mathml&#34;&gt;&lt;math xmlns=&#34;http://www.w3.org/1998/Math/MathML&#34;&gt;&lt;semantics&gt;&lt;mrow&gt;&lt;mi&gt;l&lt;/mi&gt;&lt;mi&gt;o&lt;/mi&gt;&lt;mi&gt;n&lt;/mi&gt;&lt;mi&gt;g&lt;/mi&gt;&lt;mi&gt;l&lt;/mi&gt;&lt;mi&gt;o&lt;/mi&gt;&lt;mi&gt;n&lt;/mi&gt;&lt;mi&gt;g&lt;/mi&gt;&lt;/mrow&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;long long&lt;/annotation&gt;&lt;/semantics&gt;&lt;/math&gt;&lt;/span&gt;&lt;span class=&#34;katex-html&#34; aria-hidden=&#34;true&#34;&gt;&lt;span class=&#34;base&#34;&gt;&lt;span class=&#34;strut&#34; style=&#34;height:0.8888799999999999em;vertical-align:-0.19444em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.01968em;&#34;&gt;l&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;o&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;n&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.03588em;&#34;&gt;g&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.01968em;&#34;&gt;l&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;o&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;n&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.03588em;&#34;&gt;g&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; 都存不下去的时候，高精度就派上用场了。一般的高精度时间复杂度为&lt;span class=&#34;katex&#34;&gt;&lt;span class=&#34;katex-mathml&#34;&gt;&lt;math xmlns=&#34;http://www.w3.org/1998/Math/MathML&#34;&gt;&lt;semantics&gt;&lt;mrow&gt;&lt;mi&gt;O&lt;/mi&gt;&lt;mo stretchy=&#34;false&#34;&gt;(&lt;/mo&gt;&lt;msup&gt;&lt;mi&gt;n&lt;/mi&gt;&lt;mn&gt;2&lt;/mn&gt;&lt;/msup&gt;&lt;mo stretchy=&#34;false&#34;&gt;)&lt;/mo&gt;&lt;/mrow&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;O(n^2)&lt;/annotation&gt;&lt;/semantics&gt;&lt;/math&gt;&lt;/span&gt;&lt;span class=&#34;katex-html&#34; aria-hidden=&#34;true&#34;&gt;&lt;span class=&#34;base&#34;&gt;&lt;span class=&#34;strut&#34; style=&#34;height:1.064108em;vertical-align:-0.25em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.02778em;&#34;&gt;O&lt;/span&gt;&lt;span class=&#34;mopen&#34;&gt;(&lt;/span&gt;&lt;span class=&#34;mord&#34;&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;n&lt;/span&gt;&lt;span class=&#34;msupsub&#34;&gt;&lt;span class=&#34;vlist-t&#34;&gt;&lt;span class=&#34;vlist-r&#34;&gt;&lt;span class=&#34;vlist&#34; style=&#34;height:0.8141079999999999em;&#34;&gt;&lt;span style=&#34;top:-3.063em;margin-right:0.05em;&#34;&gt;&lt;span class=&#34;pstrut&#34; style=&#34;height:2.7em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;sizing reset-size6 size3 mtight&#34;&gt;&lt;span class=&#34;mord mtight&#34;&gt;2&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class=&#34;mclose&#34;&gt;)&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; 的，优化过后的算法可以降低到&lt;span class=&#34;katex&#34;&gt;&lt;span class=&#34;katex-mathml&#34;&gt;&lt;math xmlns=&#34;http://www.w3.org/1998/Math/MathML&#34;&gt;&lt;semantics&gt;&lt;mrow&gt;&lt;mi&gt;O&lt;/mi&gt;&lt;mo stretchy=&#34;false&#34;&gt;(&lt;/mo&gt;&lt;msup&gt;&lt;mi&gt;n&lt;/mi&gt;&lt;mn&gt;1.57&lt;/mn&gt;&lt;/msup&gt;&lt;mo stretchy=&#34;false&#34;&gt;)&lt;/mo&gt;&lt;/mrow&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;O(n^{1.57})&lt;/annotation&gt;&lt;/semantics&gt;&lt;/math&gt;&lt;/span&gt;&lt;span class=&#34;katex-html&#34; aria-hidden=&#34;true&#34;&gt;&lt;span class=&#34;base&#34;&gt;&lt;span class=&#34;strut&#34; style=&#34;height:1.064108em;vertical-align:-0.25em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.02778em;&#34;&gt;O&lt;/span&gt;&lt;span class=&#34;mopen&#34;&gt;(&lt;/span&gt;&lt;span class=&#34;mord&#34;&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;n&lt;/span&gt;&lt;span class=&#34;msupsub&#34;&gt;&lt;span class=&#34;vlist-t&#34;&gt;&lt;span class=&#34;vlist-r&#34;&gt;&lt;span class=&#34;vlist&#34; style=&#34;height:0.8141079999999999em;&#34;&gt;&lt;span style=&#34;top:-3.063em;margin-right:0.05em;&#34;&gt;&lt;span class=&#34;pstrut&#34; style=&#34;height:2.7em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;sizing reset-size6 size3 mtight&#34;&gt;&lt;span class=&#34;mord mtight&#34;&gt;&lt;span class=&#34;mord mtight&#34;&gt;1&lt;/span&gt;&lt;span class=&#34;mord mtight&#34;&gt;.&lt;/span&gt;&lt;span class=&#34;mord mtight&#34;&gt;5&lt;/span&gt;&lt;span class=&#34;mord mtight&#34;&gt;7&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class=&#34;mclose&#34;&gt;)&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;，但一般的题目中用最朴素的高精度就可以解决大多数问题，因此只上了一般的高精度代码&lt;/p&gt;
&lt;h2 id=&#34;二-例题和代码&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#二-例题和代码&#34;&gt;#&lt;/a&gt; 二、例题和代码&lt;/h2&gt;
&lt;p&gt;&lt;span class=&#34;exturl&#34; data-url=&#34;aHR0cHM6Ly93d3cubHVvZ3UuY29tLmNuL3Byb2JsZW0vUDE2MDE=&#34;&gt;例题：A+B Problem plus&lt;/span&gt;&lt;/p&gt;
&lt;p&gt;&lt;span class=&#34;exturl&#34; data-url=&#34;aHR0cHM6Ly93d3cubHVvZ3UuY29tLmNuL3Byb2JsZW0vUDEzMDM=&#34;&gt;例题：A*B Problem plus&lt;/span&gt;&lt;/p&gt;
&lt;p&gt;&lt;span class=&#34;exturl&#34; data-url=&#34;aHR0cHM6Ly93d3cubHVvZ3UuY29tLmNuL3Byb2JsZW0vUDE0ODA=&#34;&gt;例题：A/B Problem plus&lt;/span&gt;&lt;/p&gt;
&lt;p&gt;Code:&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;struct biglong
&amp;#123;
    int d[N],fh;

    void print()
    &amp;#123;
        d[1]*=fh;
        for(int i=1;i&amp;lt;=d[0];i++)
            printf(&amp;quot;%d&amp;quot;,d[i]);
        printf(&amp;quot;\n&amp;quot;);
    &amp;#125;

    bool operator &amp;lt; (const biglong &amp;amp; ot) const
    &amp;#123;
        if(d[0]==ot.d[0])
        &amp;#123;
            for(int i=1;i&amp;lt;=d[0];i++)
            &amp;#123;
                if(d[i]==ot.d[i])
                    continue;
                return d[i]&amp;lt;ot.d[i];
            &amp;#125;
        &amp;#125;
        return d[0]&amp;lt;ot.d[0];
    &amp;#125;

    biglong operator + (biglong &amp;amp; ot)
    &amp;#123;
        biglong t;
        memset(t.d,0,sizeof(t.d));
        for(int a=1,b=d[0];a&amp;lt;b;a++,b--)
            swap(d[a],d[b]);
        for(int a=1,b=ot.d[0];a&amp;lt;b;a++,b--)
            swap(ot.d[a],ot.d[b]);
        int borrow=0;
        t.d[0]=max(d[0],ot.d[0]);
        for(int i=1;i&amp;lt;=t.d[0];i++)
        &amp;#123;
            t.d[i]=borrow+d[i]+ot.d[i];
            if(t.d[i]&amp;gt;=10)
                borrow=t.d[i]/10,t.d[i]%=10;
            else
                borrow=0;
        &amp;#125;
        if(borrow&amp;gt;0)
            t.d[0]++,t.d[t.d[0]]=borrow;
        for(int a=1,b=d[0];a&amp;lt;b;a++,b--)
            swap(d[a],d[b]);
        for(int a=1,b=ot.d[0];a&amp;lt;b;a++,b--)
            swap(ot.d[a],ot.d[b]);
        for(int a=1,b=t.d[0];a&amp;lt;b;a++,b--)
            swap(t.d[a],t.d[b]);
        return t;
    &amp;#125;

    biglong operator * (biglong &amp;amp; ot)
    &amp;#123;
        biglong t;
        memset(t.d,0,sizeof(t.d));
        for(int a=1,b=d[0];a&amp;lt;b;a++,b--)
            swap(d[a],d[b]);
        for(int a=1,b=ot.d[0];a&amp;lt;b;a++,b--)
            swap(ot.d[a],ot.d[b]);
        for(int i=1;i&amp;lt;=d[0];i++)
        &amp;#123;
           for(int j=1;j&amp;lt;=ot.d[0];j++)
           &amp;#123;
                t.d[i+j-1]+=d[i]*ot.d[j];
                t.d[i+j]+=t.d[i+j-1]/10;
                t.d[i+j-1]%=10;
           &amp;#125;
        &amp;#125;
        t.d[0]=d[0]+ot.d[0];
        while(t.d[t.d[0]]==0)
            t.d[0]--;
        for(int a=1,b=t.d[0];a&amp;lt;b;a++,b--)
            swap(t.d[a],t.d[b]);
        for(int a=1,b=d[0];a&amp;lt;b;a++,b--)
            swap(d[a],d[b]);
        for(int a=1,b=ot.d[0];a&amp;lt;b;a++,b--)
            swap(ot.d[a],ot.d[b]);
        return t;
    &amp;#125;
&amp;#125;;

biglong zero;

biglong cut(biglong x,biglong y)
&amp;#123;
    biglong t;
    memset(t.d,0,sizeof(t.d));
    if(x&amp;lt;y)
    &amp;#123;
        biglong temp=zero;
        temp=x,x=y,y=temp;
        t.fh=-1;
    &amp;#125;
    else
        t.fh=1;
    for(int a=1,b=x.d[0];a&amp;lt;b;a++,b--)
        swap(x.d[a],x.d[b]);
    for(int a=1,b=y.d[0];a&amp;lt;b;a++,b--)
        swap(y.d[a],y.d[b]);
    int maxn=max(x.d[0],y.d[0]);
    for(int i=1;i&amp;lt;=maxn;i++)
    &amp;#123;
        if(x.d[i]&amp;lt;y.d[i])
        &amp;#123;
            x.d[i+1]--;
            x.d[i]+=10;
        &amp;#125;
        t.d[i]=x.d[i]-y.d[i];
    &amp;#125;
    while(t.d[maxn]==0)
        maxn--;
    t.d[0]=maxn;
    if(maxn&amp;lt;0)
        memset(t.d,0,sizeof(t.d)),t.d[0]=1;
    for(int a=1,b=t.d[0];a&amp;lt;b;a++,b--)
        swap(t.d[a],t.d[b]);
    return t;
&amp;#125;

biglong mul(biglong x,int v)
&amp;#123;
    biglong ans=zero;
    for(int a=1,b=x.d[0];a&amp;lt;b;a++,b--)
        swap(x.d[a],x.d[b]);
    int borrow=0;
    for(int t=1;t&amp;lt;=x.d[0] || borrow;t++)
    &amp;#123;
        ans.d[t]=x.d[t]*v+borrow;
        borrow=ans.d[t]/10;
        ans.d[t]%=10;
        ans.d[0]++;
    &amp;#125;
    for(int a=1,b=x.d[0];a&amp;lt;b;a++,b--)
        swap(x.d[a],x.d[b]);
    for(int a=1,b=ans.d[0];a&amp;lt;b;a++,b--)
        swap(ans.d[a],ans.d[b]);
    return ans;
&amp;#125;

biglong divison(biglong x,int cut)
&amp;#123;
    biglong ans1=zero,ans2=zero;
    int borrow=0;
    for(int i=1;i&amp;lt;=x.d[0];i++)
    &amp;#123;
        ans1.d[i]=(borrow*10+x.d[i])/cut;
        borrow=(borrow*10+x.d[i])%cut;
    &amp;#125;
    int len=1;
    while(ans1.d[len]==0 &amp;amp;&amp;amp; len&amp;lt;x.d[0])
        len++;
    for(int i=1;i&amp;lt;=x.d[0]-len+1;i++)
        ans2.d[i]=ans1.d[i+len-1];
    ans2.d[0]=x.d[0]-len+1;
    return ans2;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;

 &lt;h1 id=&#34;莫队算法的模板&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#莫队算法的模板&#34;&gt;#&lt;/a&gt; 莫队算法的模板&lt;/h1&gt;
&lt;p&gt;&lt;span id=&#34;more&#34;&gt;&lt;/span&gt;&lt;/p&gt;
&lt;h2 id=&#34;一-简介&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#一-简介&#34;&gt;#&lt;/a&gt; 一、简介&lt;/h2&gt;
&lt;p&gt;莫队算法是由莫涛大神提出的一种区间操作的算法，这个算法的基础是分块和排序，整体复杂度为&lt;span class=&#34;katex&#34;&gt;&lt;span class=&#34;katex-mathml&#34;&gt;&lt;math xmlns=&#34;http://www.w3.org/1998/Math/MathML&#34;&gt;&lt;semantics&gt;&lt;mrow&gt;&lt;mi&gt;O&lt;/mi&gt;&lt;mo stretchy=&#34;false&#34;&gt;(&lt;/mo&gt;&lt;mi&gt;n&lt;/mi&gt;&lt;msqrt&gt;&lt;mi&gt;x&lt;/mi&gt;&lt;/msqrt&gt;&lt;mo stretchy=&#34;false&#34;&gt;)&lt;/mo&gt;&lt;/mrow&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;O(n\sqrt x)&lt;/annotation&gt;&lt;/semantics&gt;&lt;/math&gt;&lt;/span&gt;&lt;span class=&#34;katex-html&#34; aria-hidden=&#34;true&#34;&gt;&lt;span class=&#34;base&#34;&gt;&lt;span class=&#34;strut&#34; style=&#34;height:1.05028em;vertical-align:-0.25em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.02778em;&#34;&gt;O&lt;/span&gt;&lt;span class=&#34;mopen&#34;&gt;(&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;n&lt;/span&gt;&lt;span class=&#34;mord sqrt&#34;&gt;&lt;span class=&#34;vlist-t vlist-t2&#34;&gt;&lt;span class=&#34;vlist-r&#34;&gt;&lt;span class=&#34;vlist&#34; style=&#34;height:0.8002800000000001em;&#34;&gt;&lt;span class=&#34;svg-align&#34; style=&#34;top:-3em;&#34;&gt;&lt;span class=&#34;pstrut&#34; style=&#34;height:3em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;padding-left:0.833em;&#34;&gt;x&lt;/span&gt;&lt;/span&gt;&lt;span style=&#34;top:-2.76028em;&#34;&gt;&lt;span class=&#34;pstrut&#34; style=&#34;height:3em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;hide-tail&#34; style=&#34;min-width:0.853em;height:1.08em;&#34;&gt;&lt;svg width=&#39;400em&#39; height=&#39;1.08em&#39; viewBox=&#39;0 0 400000 1080&#39; preserveAspectRatio=&#39;xMinYMin slice&#39;&gt;&lt;path d=&#39;M95,702
