M834 80h400000v40h-400000z&#39;/&gt;&lt;/svg&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class=&#34;vlist-s&#34;&gt;​&lt;/span&gt;&lt;/span&gt;&lt;span class=&#34;vlist-r&#34;&gt;&lt;span class=&#34;vlist&#34; style=&#34;height:0.23972em;&#34;&gt;&lt;span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class=&#34;mclose&#34;&gt;)&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;
&lt;p&gt;这里不细讲莫队的原理细节，原理和细节请移步另一篇文章，上例题和代码&lt;/p&gt;
&lt;p&gt;&lt;span class=&#34;exturl&#34; data-url=&#34;aHR0cHM6Ly93d3cubHVvZ3UuY29tLmNuL3Byb2JsZW0vUDE0OTQ=&#34;&gt;例题：小 Z 的袜子&lt;/span&gt;&lt;/p&gt;
&lt;p&gt;Code:&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;#include &amp;lt;bits/stdc++.h&amp;gt;
using namespace std;
const int N=100001;
long long block,n,m,d[N],ans,answer[N][2],cnt[2*N],K;
struct node
&amp;#123;
	long long block_id,l,r,id;
	bool operator &amp;lt; (const node &amp;amp; ot) const
	&amp;#123;
		if(block_id==ot.block_id)
			return r&amp;lt;ot.r;
		return block_id&amp;lt;ot.block_id;
	&amp;#125;
&amp;#125;que[N],ys[N];
long long gcd(long long a,long long b)
&amp;#123;
    return b?gcd(b,a%b):a;
&amp;#125;
inline void read(long long &amp;amp; a)
&amp;#123;
    char ch;
    while(!(((ch=getchar())&amp;gt;=&#39;0&#39;)&amp;amp;&amp;amp;(ch&amp;lt;=&#39;9&#39;)));
    a=ch-&#39;0&#39;;
    while(((ch=getchar())&amp;gt;=&#39;0&#39;)&amp;amp;&amp;amp;(ch&amp;lt;=&#39;9&#39;))(a*=10)+=ch-&#39;0&#39;;
&amp;#125;
void add(long long x)
&amp;#123;
	ans+=cnt[d[x]];
	cnt[d[x]]++;
	return;
&amp;#125;
void del(long long x)
&amp;#123;
	if(cnt[d[x]]&amp;gt;1)
		ans-=cnt[d[x]]-1;
	cnt[d[x]]--;
	return;
&amp;#125;
int main()
&amp;#123;
	read(n),read(m);
	for(long long i=1;i&amp;lt;=n;i++)
		read(d[i]);
	block=n/sqrt(m*2/3);
	for(long long i=1;i&amp;lt;=m;i++)
	&amp;#123;
		read(que[i].l),read(que[i].r);
		que[i].id=i;
		ys[i].l=que[i].l,ys[i].r=que[i].r;
		if(que[i].l%block==0)
			que[i].block_id=que[i].l/block;
		else
			que[i].block_id=que[i].l/block+1;
	&amp;#125;
	sort(que+1,que+1+m);
	long long l=1,r=0;
	for(long long i=1;i&amp;lt;=m;i++)
	&amp;#123;
		if(que[i].l==que[i].r)
			continue;
		while(l&amp;gt;que[i].l)
			add(--l);
		while(r&amp;lt;que[i].r)
			add(++r);
		while(l&amp;lt;que[i].l)
			del(l++);
		while(r&amp;gt;que[i].r)
			del(r--);
		answer[que[i].id][0]=ans,answer[que[i].id][1]=(que[i].r-que[i].l+1)*(que[i].r-que[i].l)/2;
	&amp;#125;
	for(long long i=1;i&amp;lt;=m;i++)
	&amp;#123;
		if(ys[i].l==ys[i].r)
		&amp;#123;
			printf(&amp;quot;0/1\n&amp;quot;);
			continue;
		&amp;#125;
		long long g=gcd(answer[i][0],answer[i][1]);
		if(answer[i][0]!=0)
			printf(&amp;quot;%lld/%lld\n&amp;quot;,answer[i][0]/g,answer[i][1]/g);
		else
			printf(&amp;quot;0/1\n&amp;quot;);
	&amp;#125;
	return 0;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;

 &lt;h1 id=&#34;扩展欧几里得-gcd-快速幂模板&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#扩展欧几里得-gcd-快速幂模板&#34;&gt;#&lt;/a&gt; 扩展欧几里得、gcd、快速幂模板&lt;/h1&gt;
&lt;p&gt;&lt;span id=&#34;more&#34;&gt;&lt;/span&gt;&lt;/p&gt;
&lt;h2 id=&#34;一-gcd模板&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#一-gcd模板&#34;&gt;#&lt;/a&gt; 一、gcd 模板&lt;/h2&gt;
&lt;p&gt;gcd 全称就是最大公约数，lcm 是最小公倍数，而 lcm 可以很容易根据 gcd 得出，所以这里只给出 gcd 的代码&lt;/p&gt;
&lt;p&gt;Code:&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;typedef long long ll;
ll gcd(ll a,ll b)
&amp;#123;
    return b?gcd(b,a%b):a;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
&lt;h2 id=&#34;二-扩展欧几里得&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#二-扩展欧几里得&#34;&gt;#&lt;/a&gt; 二、扩展欧几里得&lt;/h2&gt;
&lt;p&gt;先简单介绍一下其作用：&lt;/p&gt;
&lt;p&gt;1、求解不定方程&lt;/p&gt;
&lt;p&gt;2、求解模的逆元&lt;/p&gt;
&lt;p&gt;3、求解线性同余方程&lt;/p&gt;
&lt;p&gt;&lt;span class=&#34;exturl&#34; data-url=&#34;aHR0cHM6Ly93d3cubHVvZ3UuY29tLmNuL3Byb2JsZW0vUDI2MTM=&#34;&gt;例题：有理数取余&lt;/span&gt;&lt;/p&gt;
&lt;p&gt;Code:&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;#include &amp;lt;bits/stdc++.h&amp;gt;
using namespace std;
const int mod=19260817;
const int N=10001;
typedef long long ll;
int a[N],b[N];
ll expand(ll a,ll b,ll &amp;amp;x,ll &amp;amp;y)
&amp;#123;
    if(b==0)
    &amp;#123;
        x=1,y=0;
        return a;
    &amp;#125;
    ll r=expand(b,a%b,x,y);
    ll t=y;
    y=x-(a/b)*y;
    x=t;
    return r;
&amp;#125;
int main()
&amp;#123;
	char ch[N];
	scanf(&amp;quot;%s&amp;quot;,ch);
	int len=strlen(ch);
	ll aa=0,bb=0;
	for(int i=0;i&amp;lt;len;i++)
		aa=(aa*10+ch[i]-&#39;0&#39;)%mod;
	scanf(&amp;quot;%s&amp;quot;,ch);
	len=strlen(ch);
	for(int i=0;i&amp;lt;len;i++)
		bb=(bb*10+ch[i]-&#39;0&#39;)%mod;
	ll x0,y0;
	expand(bb,mod,x0,y0);
	if(bb==0)
		printf(&amp;quot;Angry!\n&amp;quot;);
	else
		printf(&amp;quot;%lld\n&amp;quot;,(aa*((x0%mod+mod)%mod))%mod);
	return 0;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;原理方面请移步另一篇文章&lt;/p&gt;
&lt;h2 id=&#34;三-快速幂&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#三-快速幂&#34;&gt;#&lt;/a&gt; 三、快速幂&lt;/h2&gt;
&lt;p&gt;正常的求&lt;span class=&#34;katex&#34;&gt;&lt;span class=&#34;katex-mathml&#34;&gt;&lt;math xmlns=&#34;http://www.w3.org/1998/Math/MathML&#34;&gt;&lt;semantics&gt;&lt;mrow&gt;&lt;msup&gt;&lt;mi&gt;x&lt;/mi&gt;&lt;mi&gt;n&lt;/mi&gt;&lt;/msup&gt;&lt;/mrow&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;x^n&lt;/annotation&gt;&lt;/semantics&gt;&lt;/math&gt;&lt;/span&gt;&lt;span class=&#34;katex-html&#34; aria-hidden=&#34;true&#34;&gt;&lt;span class=&#34;base&#34;&gt;&lt;span class=&#34;strut&#34; style=&#34;height:0.664392em;vertical-align:0em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;mord&#34;&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;x&lt;/span&gt;&lt;span class=&#34;msupsub&#34;&gt;&lt;span class=&#34;vlist-t&#34;&gt;&lt;span class=&#34;vlist-r&#34;&gt;&lt;span class=&#34;vlist&#34; style=&#34;height:0.664392em;&#34;&gt;&lt;span style=&#34;top:-3.063em;margin-right:0.05em;&#34;&gt;&lt;span class=&#34;pstrut&#34; style=&#34;height:2.7em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;sizing reset-size6 size3 mtight&#34;&gt;&lt;span class=&#34;mord mathnormal mtight&#34;&gt;n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; 需要&lt;span class=&#34;katex&#34;&gt;&lt;span class=&#34;katex-mathml&#34;&gt;&lt;math xmlns=&#34;http://www.w3.org/1998/Math/MathML&#34;&gt;&lt;semantics&gt;&lt;mrow&gt;&lt;mi&gt;O&lt;/mi&gt;&lt;mo stretchy=&#34;false&#34;&gt;(&lt;/mo&gt;&lt;mi&gt;n&lt;/mi&gt;&lt;mo stretchy=&#34;false&#34;&gt;)&lt;/mo&gt;&lt;/mrow&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;O(n)&lt;/annotation&gt;&lt;/semantics&gt;&lt;/math&gt;&lt;/span&gt;&lt;span class=&#34;katex-html&#34; aria-hidden=&#34;true&#34;&gt;&lt;span class=&#34;base&#34;&gt;&lt;span class=&#34;strut&#34; style=&#34;height:1em;vertical-align:-0.25em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.02778em;&#34;&gt;O&lt;/span&gt;&lt;span class=&#34;mopen&#34;&gt;(&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;n&lt;/span&gt;&lt;span class=&#34;mclose&#34;&gt;)&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; 的时间复杂度，快速幂就是把&lt;span class=&#34;katex&#34;&gt;&lt;span class=&#34;katex-mathml&#34;&gt;&lt;math xmlns=&#34;http://www.w3.org/1998/Math/MathML&#34;&gt;&lt;semantics&gt;&lt;mrow&gt;&lt;mi&gt;O&lt;/mi&gt;&lt;mo stretchy=&#34;false&#34;&gt;(&lt;/mo&gt;&lt;mi&gt;n&lt;/mi&gt;&lt;mo stretchy=&#34;false&#34;&gt;)&lt;/mo&gt;&lt;/mrow&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;O(n)&lt;/annotation&gt;&lt;/semantics&gt;&lt;/math&gt;&lt;/span&gt;&lt;span class=&#34;katex-html&#34; aria-hidden=&#34;true&#34;&gt;&lt;span class=&#34;base&#34;&gt;&lt;span class=&#34;strut&#34; style=&#34;height:1em;vertical-align:-0.25em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.02778em;&#34;&gt;O&lt;/span&gt;&lt;span class=&#34;mopen&#34;&gt;(&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;n&lt;/span&gt;&lt;span class=&#34;mclose&#34;&gt;)&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; 时间复杂度缩减为&lt;span class=&#34;katex&#34;&gt;&lt;span class=&#34;katex-mathml&#34;&gt;&lt;math xmlns=&#34;http://www.w3.org/1998/Math/MathML&#34;&gt;&lt;semantics&gt;&lt;mrow&gt;&lt;mi&gt;O&lt;/mi&gt;&lt;mo stretchy=&#34;false&#34;&gt;(&lt;/mo&gt;&lt;mi&gt;l&lt;/mi&gt;&lt;mi&gt;o&lt;/mi&gt;&lt;mi&gt;g&lt;/mi&gt;&lt;mi&gt;n&lt;/mi&gt;&lt;mo stretchy=&#34;false&#34;&gt;)&lt;/mo&gt;&lt;/mrow&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;O(logn)&lt;/annotation&gt;&lt;/semantics&gt;&lt;/math&gt;&lt;/span&gt;&lt;span class=&#34;katex-html&#34; aria-hidden=&#34;true&#34;&gt;&lt;span class=&#34;base&#34;&gt;&lt;span class=&#34;strut&#34; style=&#34;height:1em;vertical-align:-0.25em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.02778em;&#34;&gt;O&lt;/span&gt;&lt;span class=&#34;mopen&#34;&gt;(&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.01968em;&#34;&gt;l&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;o&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.03588em;&#34;&gt;g&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;n&lt;/span&gt;&lt;span class=&#34;mclose&#34;&gt;)&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; 的算法，本质上运用了分治的思想&lt;/p&gt;
&lt;p&gt;Code:&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;typedef long long ll;
ll mod; 
ll quickpow(ll x,ll zs)
&amp;#123;
    ll ans=0;
    while(zs)
    &amp;#123;
        if(zs&amp;amp;1)
        &amp;#123;
            ans=ans*x%mod;
            ans%=mod;
        &amp;#125;
        x*=x;
        x%=mod;
        zs/=2;
    &amp;#125;
    return ans%mod;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;

 &lt;h1 id=&#34;最小生成树模板&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#最小生成树模板&#34;&gt;#&lt;/a&gt; 最小生成树模板&lt;/h1&gt;
&lt;p&gt;&lt;span id=&#34;more&#34;&gt;&lt;/span&gt;&lt;/p&gt;
&lt;p&gt;最小生成树有&lt;span class=&#34;katex&#34;&gt;&lt;span class=&#34;katex-mathml&#34;&gt;&lt;math xmlns=&#34;http://www.w3.org/1998/Math/MathML&#34;&gt;&lt;semantics&gt;&lt;mrow&gt;&lt;mi&gt;p&lt;/mi&gt;&lt;mi&gt;r&lt;/mi&gt;&lt;mi&gt;i&lt;/mi&gt;&lt;mi&gt;m&lt;/mi&gt;&lt;mi&gt;e&lt;/mi&gt;&lt;/mrow&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;prime&lt;/annotation&gt;&lt;/semantics&gt;&lt;/math&gt;&lt;/span&gt;&lt;span class=&#34;katex-html&#34; aria-hidden=&#34;true&#34;&gt;&lt;span class=&#34;base&#34;&gt;&lt;span class=&#34;strut&#34; style=&#34;height:0.85396em;vertical-align:-0.19444em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;p&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.02778em;&#34;&gt;r&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;i&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;m&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;e&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; 算法和&lt;span class=&#34;katex&#34;&gt;&lt;span class=&#34;katex-mathml&#34;&gt;&lt;math xmlns=&#34;http://www.w3.org/1998/Math/MathML&#34;&gt;&lt;semantics&gt;&lt;mrow&gt;&lt;mi&gt;k&lt;/mi&gt;&lt;mi&gt;r&lt;/mi&gt;&lt;mi&gt;u&lt;/mi&gt;&lt;mi&gt;s&lt;/mi&gt;&lt;mi&gt;k&lt;/mi&gt;&lt;mi&gt;a&lt;/mi&gt;&lt;mi&gt;l&lt;/mi&gt;&lt;/mrow&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;kruskal&lt;/annotation&gt;&lt;/semantics&gt;&lt;/math&gt;&lt;/span&gt;&lt;span class=&#34;katex-html&#34; aria-hidden=&#34;true&#34;&gt;&lt;span class=&#34;base&#34;&gt;&lt;span class=&#34;strut&#34; style=&#34;height:0.69444em;vertical-align:0em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.03148em;&#34;&gt;k&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.02778em;&#34;&gt;r&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;u&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;s&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.03148em;&#34;&gt;k&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;a&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.01968em;&#34;&gt;l&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; 算法，本篇暂时贴了克鲁斯卡尔算法写的生成树代码，算法分析请移步另一篇文章&lt;/p&gt;
&lt;p&gt;&lt;span class=&#34;exturl&#34; data-url=&#34;aHR0cHM6Ly93d3cubHVvZ3UuY29tLmNuL3Byb2JsZW0vUDMzNjY=&#34;&gt;例题：最小生成树&lt;/span&gt;&lt;/p&gt;
&lt;p&gt;Code:&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;#include &amp;lt;bits/stdc++.h&amp;gt;
using namespace std;
const int N=5001;
struct node
&amp;#123;
    int a;
    int b;
    int len;
    node(int aa,int bb,int ll)
    &amp;#123;
        a=aa;
        b=bb;
        len=ll;
    &amp;#125;
    node()
    &amp;#123;
    &amp;#125;
    bool operator &amp;lt; (const node &amp;amp; ot) const
    &amp;#123;
        return len&amp;lt;ot.len;
    &amp;#125;
&amp;#125;;
int n,m,sum,fa[N],cnt;
vector &amp;lt;node &amp;gt; g;
int find(int f)
&amp;#123;
    if(fa[f]==f)
        return f;
    fa[f]=find(fa[f]);
    return fa[f];
&amp;#125;
void hb(int a,int b)
&amp;#123;
    int f1=find(a),f2=find(b);
    if(f1!=f2)
        fa[f2]=f1;
&amp;#125;
int main()
&amp;#123;
    scanf(&amp;quot;%d %d&amp;quot;,&amp;amp;n,&amp;amp;m);
    for(int i=1;i&amp;lt;=m;i++)
    &amp;#123;
        int x,y,z;
        scanf(&amp;quot;%d %d %d&amp;quot;,&amp;amp;x,&amp;amp;y,&amp;amp;z);
        g.push_back(node(x,y,z)),sum+=z;
    &amp;#125;
    sort(g.begin(),g.end());
    int ans=0;
    for(int i=1;i&amp;lt;=n;i++)
        fa[i]=i;
    for(int i=0;i&amp;lt;m;i++)
    &amp;#123;
        if(cnt==m-1)
            break;
        int a=g[i].a,b=g[i].b,len=g[i].len;
        if(find(a)!=find(b))
        &amp;#123;
            hb(a,b);
            cnt++;
            ans+=len;
        &amp;#125;
    &amp;#125;
    printf(&amp;quot;%d\n&amp;quot;,ans);
    return 0;
 &amp;#125; 
&lt;/code&gt;&lt;/pre&gt;

 &lt;h1 id=&#34;tarjan缩点-割点和桥的模板&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#tarjan缩点-割点和桥的模板&#34;&gt;#&lt;/a&gt; Tarjan 缩点、割点和桥的模板&lt;/h1&gt;
&lt;p&gt;&lt;span id=&#34;more&#34;&gt;&lt;/span&gt;&lt;/p&gt;
&lt;h2 id=&#34;一-缩点&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#一-缩点&#34;&gt;#&lt;/a&gt; 一、缩点&lt;/h2&gt;
&lt;p&gt;缩点的前提是了解强联通分量，强联通分量的定义是强联通分量中的每一个点都可以到达另一个强联通分量中的节点，缩点就是把一个强联通分量缩成一个节点来帮助我们更好完成题目任务的一种方法&lt;/p&gt;
&lt;p&gt;&lt;span class=&#34;exturl&#34; data-url=&#34;aHR0cHM6Ly93d3cubHVvZ3UuY29tLmNuL3Byb2JsZW0vUDIzNDE=&#34;&gt;例题：受欢迎的牛 G&lt;/span&gt;&lt;/p&gt;
&lt;p&gt;Code:&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;#include &amp;lt;bits/stdc++.h&amp;gt;
using namespace std;
const int N=20001;
stack &amp;lt;int  &amp;gt;s;
vector &amp;lt;int &amp;gt; g[N];
int low[N],dfn[N],col[N],n,m,cnt,T,count0[N],tot,in[N],out[N];
bool flag[N];
void tarjan(int pos)
&amp;#123;
	dfn[pos]=low[pos]=++cnt;
	s.push(pos);
	int size=g[pos].size();
	flag[pos]=true;
	for(int i=0;i&amp;lt;size;i++)
	&amp;#123;
		int to=g[pos][i];
		if(!dfn[to])
		&amp;#123;
			tarjan(to);
			low[pos]=min(low[pos],low[to]);
		&amp;#125;
		else if(flag[to])
			low[pos]=min(low[pos],low[to]);
	&amp;#125;
	if(dfn[pos]==low[pos])
	&amp;#123;
		flag[pos]=false;
		col[pos]=++tot;
		while(!s.empty() &amp;amp;&amp;amp; s.top()!=pos)
		&amp;#123;
			flag[s.top()]=false;
			col[s.top()]=tot;
			s.pop();
		&amp;#125;
		s.pop();
	&amp;#125;
	return;
&amp;#125;
int main()
&amp;#123;
	scanf(&amp;quot;%d %d&amp;quot;,&amp;amp;n,&amp;amp;m);
	for(int i=1;i&amp;lt;=m;i++)
	&amp;#123;
		int x,y;
		scanf(&amp;quot;%d %d&amp;quot;,&amp;amp;x,&amp;amp;y);
		g[x].push_back(y);
	&amp;#125;
	for(int i=1;i&amp;lt;=n;i++)
		g[i].push_back(i);
	for(int i=1;i&amp;lt;=n;i++)
	&amp;#123;
		if(!dfn[i])
			tarjan(i);
	&amp;#125;
	for(int i=1;i&amp;lt;=n;i++)
    	count0[col[i]]++;
    for(int i=1;i&amp;lt;=n;i++)
    &amp;#123;
    	int size=g[i].size();
    	for(int j=0;j&amp;lt;size;j++)
    	&amp;#123;
    		if(col[i]!=col[g[i][j]])
    			in[col[g[i][j]]]=1,out[col[i]]=1;
		&amp;#125;
	&amp;#125;
	int anspos=0,ans=0;
	for(int i=1;i&amp;lt;=tot;i++)
	&amp;#123;
		if(!out[i])
		&amp;#123;
			ans++;
			anspos=i;
		&amp;#125;
	&amp;#125;
	if(ans&amp;gt;1)
		printf(&amp;quot;0\n&amp;quot;);
	else
		printf(&amp;quot;%d\n&amp;quot;,count0[anspos]);
	return 0;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
&lt;h2 id=&#34;二-割点&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#二-割点&#34;&gt;#&lt;/a&gt; 二、割点&lt;/h2&gt;
&lt;p&gt;割点的定义是如果一张连通图删去此节点，那么这张连通图变为不连通图，此节点就被称为割点&lt;/p&gt;
&lt;p&gt;&lt;span class=&#34;exturl&#34; data-url=&#34;aHR0cHM6Ly93d3cubHVvZ3UuY29tLmNuL3Byb2JsZW0vUDMzODg=&#34;&gt;例题：割点&lt;/span&gt;&lt;/p&gt;
&lt;p&gt;Code:&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;#include &amp;lt;bits/stdc++.h&amp;gt;
using namespace std;
const int N=200001;
int n,m,dfn[N],low[N],root,cnt;
bool flag[N],cut[N];
vector &amp;lt;int &amp;gt; g[N];
void tarjan(int cur)
&amp;#123;
	dfn[cur]=low[cur]=++cnt;
	int size=g[cur].size();
	int flag=0;
	for(int i=0;i&amp;lt;size;i++)
	&amp;#123;
		int to=g[cur][i];
		if(!dfn[to])
		&amp;#123;
			tarjan(to);
			low[cur]=min(low[cur],low[to]);
			if(low[to]&amp;gt;=dfn[cur])
			&amp;#123;
				flag++;
				if(cur!=root || flag&amp;gt;1)
					cut[cur]=true;
			&amp;#125;
		&amp;#125;
		else
			low[cur]=min(low[cur],dfn[to]);
	&amp;#125;
	return;
&amp;#125;
int main()
&amp;#123;
	scanf(&amp;quot;%d %d&amp;quot;,&amp;amp;n,&amp;amp;m);
	for(int i=1;i&amp;lt;=m;i++)
	&amp;#123;
		int a,b;
		scanf(&amp;quot;%d %d&amp;quot;,&amp;amp;a,&amp;amp;b);
		g[a].push_back(b);
		g[b].push_back(a);
	&amp;#125;
	for(int i=1;i&amp;lt;=n;i++)
		if(!dfn[i])
			root=i,tarjan(i);
	long long ans=0;
	for(int i=1;i&amp;lt;=n;i++)
		if(cut[i])
			ans++;
	printf(&amp;quot;%lld\n&amp;quot;,ans);
	for(int i=1;i&amp;lt;=n;i++)
		if(cut[i])
			printf(&amp;quot;%d &amp;quot;,i);
	return 0;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
&lt;h2 id=&#34;三-桥&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#三-桥&#34;&gt;#&lt;/a&gt; 三、桥&lt;/h2&gt;
&lt;p&gt;桥的定义和割点很像，区别在于割点是一个点，而桥是一条边，也就是一张连通图少了一个桥就会变成非连通图&lt;/p&gt;
&lt;p&gt;没有好的例题，直接上代码：&lt;/p&gt;
&lt;p&gt;Code:&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;struct node
&amp;#123;
    int to,id;
    node(int tt,int ii)
    &amp;#123;
        to=tt,id=ii;
    &amp;#125;
    node()
    &amp;#123;
    &amp;#125;
&amp;#125;;
vector &amp;lt;node &amp;gt; g[N];
int dfn[N],low[N];
bool qiao[N];

void tarjan(int cur)
&amp;#123;
    dfn[cur]=low[cur]=++cnt;
    int size=g[cur].size();
    for(int i=0;i&amp;lt;size;i++)
    &amp;#123;
        int to=g[cur][i].to;
        if(!dfn[to])
        &amp;#123;
            tarjan(to);
            low[cur]=min(low[cur],low[to]);
            if(low[to]&amp;gt;dfn[cur])
                qiao[g[cur][i].id]=true;
        &amp;#125;
        else
            low[cur]=min(low[cur],dfn[to]);
    &amp;#125;
    return;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;本篇文章只贴模板，具体分析移步另一篇文章&lt;/p&gt;

 &lt;h1 id=&#34;树状数组模板&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#树状数组模板&#34;&gt;#&lt;/a&gt; 树状数组模板&lt;/h1&gt;
&lt;p&gt;&lt;span id=&#34;more&#34;&gt;&lt;/span&gt;&lt;/p&gt;
&lt;h2 id=&#34;一-树状数组大致介绍&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#一-树状数组大致介绍&#34;&gt;#&lt;/a&gt; 一、树状数组大致介绍&lt;/h2&gt;
&lt;p&gt;1、树状数组就是模拟树形结构的一个数组&lt;/p&gt;
&lt;p&gt;2、树状数组解决的问题大部分是区间上的更新以及求和&lt;/p&gt;
&lt;p&gt;3、树状数组的修改和查询的复杂度都是&lt;span class=&#34;katex&#34;&gt;&lt;span class=&#34;katex-mathml&#34;&gt;&lt;math xmlns=&#34;http://www.w3.org/1998/Math/MathML&#34;&gt;&lt;semantics&gt;&lt;mrow&gt;&lt;mi&gt;O&lt;/mi&gt;&lt;mo stretchy=&#34;false&#34;&gt;(&lt;/mo&gt;&lt;mi&gt;l&lt;/mi&gt;&lt;mi&gt;o&lt;/mi&gt;&lt;mi&gt;g&lt;/mi&gt;&lt;mi&gt;n&lt;/mi&gt;&lt;mo stretchy=&#34;false&#34;&gt;)&lt;/mo&gt;&lt;/mrow&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;O(logn)&lt;/annotation&gt;&lt;/semantics&gt;&lt;/math&gt;&lt;/span&gt;&lt;span class=&#34;katex-html&#34; aria-hidden=&#34;true&#34;&gt;&lt;span class=&#34;base&#34;&gt;&lt;span class=&#34;strut&#34; style=&#34;height:1em;vertical-align:-0.25em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.02778em;&#34;&gt;O&lt;/span&gt;&lt;span class=&#34;mopen&#34;&gt;(&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.01968em;&#34;&gt;l&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;o&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.03588em;&#34;&gt;g&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;n&lt;/span&gt;&lt;span class=&#34;mclose&#34;&gt;)&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;
&lt;p&gt;4、树状数组的大致形状如下图：&lt;/p&gt;
&lt;p&gt;&lt;img data-src=&#34;https://s2.loli.net/2023/10/26/a1CPq9TMpNJBUi8.png&#34; alt=&#34;树状数组&#34; /&gt;&lt;/p&gt;
&lt;p&gt;具体原理请移步另一篇文章，本篇只搬运模板&lt;/p&gt;
&lt;h2 id=&#34;二-例题和模板&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#二-例题和模板&#34;&gt;#&lt;/a&gt; 二、例题和模板&lt;/h2&gt;
&lt;p&gt;&lt;span class=&#34;exturl&#34; data-url=&#34;aHR0cHM6Ly93d3cubHVvZ3UuY29tLmNuL3Byb2JsZW0vUDMzNjg=&#34;&gt;例题：树状数组&lt;/span&gt;&lt;/p&gt;
&lt;p&gt;Code:&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;#include &amp;lt;cstdio&amp;gt;
#include &amp;lt;cstdlib&amp;gt;
using namespace std;
int n;
const int N=500001;
int d[N];
int m;
int cf[N];
int lowbit(int x)
&amp;#123;
    return x &amp;amp; -x;
&amp;#125;
int add(int wz,int v)
&amp;#123;
    while(wz&amp;lt;=n)
    &amp;#123;
        cf[wz]+=v;
        wz+=lowbit(wz);
    &amp;#125;
&amp;#125;
int get_ans(int x)
&amp;#123;
    int cnt=0;
    while(x&amp;gt;0)
    &amp;#123;
        cnt+=cf[x];
        x-=lowbit(x);
    &amp;#125;
    return cnt;
&amp;#125;
int main()
&amp;#123;
    scanf(&amp;quot;%d %d&amp;quot;,&amp;amp;n,&amp;amp;m);
    int c;
    for(int i=1;i&amp;lt;=n;i++)
    &amp;#123;
        scanf(&amp;quot;%d&amp;quot;,&amp;amp;c);
        add(i,c);
        add(i+1,-c);
    &amp;#125;
    for(int i=0;i&amp;lt;m;i++)
    &amp;#123;
        int op;
        scanf(&amp;quot;%d&amp;quot;,&amp;amp;op);
        if(op==1)
        &amp;#123;
            int l,r,z;
            scanf(&amp;quot; %d %d %d&amp;quot;,&amp;amp;l,&amp;amp;r,&amp;amp;z);
            add(l,z);
            add(r+1,-z);
        &amp;#125;
        else
        &amp;#123;
            int x;
            scanf(&amp;quot;%d&amp;quot;,&amp;amp;x);
            printf(&amp;quot;%d\n&amp;quot;,get_ans(x));
        &amp;#125;
    &amp;#125;
    return 0;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;

 &lt;h1 id=&#34;rmq模板&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#rmq模板&#34;&gt;#&lt;/a&gt; RMQ 模板&lt;/h1&gt;
&lt;p&gt;&lt;span id=&#34;more&#34;&gt;&lt;/span&gt;&lt;/p&gt;
&lt;h2 id=&#34;一-rmq简介&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#一-rmq简介&#34;&gt;#&lt;/a&gt; 一、RMQ 简介&lt;/h2&gt;
&lt;p&gt;中文全名为区间最值查询，有人说区间最值不就循环扫一遍就出来了吗，好想法。但是，如果区间长度为 100000 以上而且需要连续查询 m 次呢&lt;/p&gt;
&lt;p&gt;显然你会看到满屏的 &amp;lt;font color=&amp;quot;#ffc116&amp;quot; size=&amp;quot;6&amp;quot;&amp;gt;TLE&amp;lt;/font&amp;gt;，谁不喜欢绿色的 &amp;lt; font color=&amp;quot;#52a41a&amp;quot; size=&amp;quot;6&amp;quot;&amp;gt;AC&amp;lt;/font &amp;gt; 呢？所以 RMQ 算法就是把黄色绿掉的算法&lt;/p&gt;
&lt;p&gt;RMQ 算法一般预处理时间较长，时间复杂度为&lt;span class=&#34;katex&#34;&gt;&lt;span class=&#34;katex-mathml&#34;&gt;&lt;math xmlns=&#34;http://www.w3.org/1998/Math/MathML&#34;&gt;&lt;semantics&gt;&lt;mrow&gt;&lt;mi&gt;O&lt;/mi&gt;&lt;mo stretchy=&#34;false&#34;&gt;(&lt;/mo&gt;&lt;mi&gt;n&lt;/mi&gt;&lt;mi&gt;l&lt;/mi&gt;&lt;mi&gt;o&lt;/mi&gt;&lt;mi&gt;g&lt;/mi&gt;&lt;mi&gt;n&lt;/mi&gt;&lt;mo stretchy=&#34;false&#34;&gt;)&lt;/mo&gt;&lt;/mrow&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;O(nlogn)&lt;/annotation&gt;&lt;/semantics&gt;&lt;/math&gt;&lt;/span&gt;&lt;span class=&#34;katex-html&#34; aria-hidden=&#34;true&#34;&gt;&lt;span class=&#34;base&#34;&gt;&lt;span class=&#34;strut&#34; style=&#34;height:1em;vertical-align:-0.25em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.02778em;&#34;&gt;O&lt;/span&gt;&lt;span class=&#34;mopen&#34;&gt;(&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;n&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.01968em;&#34;&gt;l&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;o&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.03588em;&#34;&gt;g&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;n&lt;/span&gt;&lt;span class=&#34;mclose&#34;&gt;)&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;, 但查询速度很快，只需要&lt;span class=&#34;katex&#34;&gt;&lt;span class=&#34;katex-mathml&#34;&gt;&lt;math xmlns=&#34;http://www.w3.org/1998/Math/MathML&#34;&gt;&lt;semantics&gt;&lt;mrow&gt;&lt;mi&gt;O&lt;/mi&gt;&lt;mo stretchy=&#34;false&#34;&gt;(&lt;/mo&gt;&lt;mn&gt;1&lt;/mn&gt;&lt;mo stretchy=&#34;false&#34;&gt;)&lt;/mo&gt;&lt;/mrow&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;O(1)&lt;/annotation&gt;&lt;/semantics&gt;&lt;/math&gt;&lt;/span&gt;&lt;span class=&#34;katex-html&#34; aria-hidden=&#34;true&#34;&gt;&lt;span class=&#34;base&#34;&gt;&lt;span class=&#34;strut&#34; style=&#34;height:1em;vertical-align:-0.25em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.02778em;&#34;&gt;O&lt;/span&gt;&lt;span class=&#34;mopen&#34;&gt;(&lt;/span&gt;&lt;span class=&#34;mord&#34;&gt;1&lt;/span&gt;&lt;span class=&#34;mclose&#34;&gt;)&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;，&lt;span class=&#34;katex&#34;&gt;&lt;span class=&#34;katex-mathml&#34;&gt;&lt;math xmlns=&#34;http://www.w3.org/1998/Math/MathML&#34;&gt;&lt;semantics&gt;&lt;mrow&gt;&lt;mi&gt;R&lt;/mi&gt;&lt;mi&gt;M&lt;/mi&gt;&lt;mi&gt;Q&lt;/mi&gt;&lt;/mrow&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;RMQ&lt;/annotation&gt;&lt;/semantics&gt;&lt;/math&gt;&lt;/span&gt;&lt;span class=&#34;katex-html&#34; aria-hidden=&#34;true&#34;&gt;&lt;span class=&#34;base&#34;&gt;&lt;span class=&#34;strut&#34; style=&#34;height:0.8777699999999999em;vertical-align:-0.19444em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.00773em;&#34;&gt;R&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.10903em;&#34;&gt;M&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;Q&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; 本质上还是用到了倍增的思想，记录连续&lt;span class=&#34;katex&#34;&gt;&lt;span class=&#34;katex-mathml&#34;&gt;&lt;math xmlns=&#34;http://www.w3.org/1998/Math/MathML&#34;&gt;&lt;semantics&gt;&lt;mrow&gt;&lt;msup&gt;&lt;mn&gt;2&lt;/mn&gt;&lt;mi&gt;j&lt;/mi&gt;&lt;/msup&gt;&lt;/mrow&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;2^j&lt;/annotation&gt;&lt;/semantics&gt;&lt;/math&gt;&lt;/span&gt;&lt;span class=&#34;katex-html&#34; aria-hidden=&#34;true&#34;&gt;&lt;span class=&#34;base&#34;&gt;&lt;span class=&#34;strut&#34; style=&#34;height:0.824664em;vertical-align:0em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;mord&#34;&gt;&lt;span class=&#34;mord&#34;&gt;2&lt;/span&gt;&lt;span class=&#34;msupsub&#34;&gt;&lt;span class=&#34;vlist-t&#34;&gt;&lt;span class=&#34;vlist-r&#34;&gt;&lt;span class=&#34;vlist&#34; style=&#34;height:0.824664em;&#34;&gt;&lt;span style=&#34;top:-3.063em;margin-right:0.05em;&#34;&gt;&lt;span class=&#34;pstrut&#34; style=&#34;height:2.7em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;sizing reset-size6 size3 mtight&#34;&gt;&lt;span class=&#34;mord mathnormal mtight&#34; style=&#34;margin-right:0.05724em;&#34;&gt;j&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; 个数的最值，因此提高查找效率&lt;/p&gt;
&lt;h2 id=&#34;二-例题及代码&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#二-例题及代码&#34;&gt;#&lt;/a&gt; 二、例题及代码&lt;/h2&gt;
&lt;p&gt;&lt;span class=&#34;exturl&#34; data-url=&#34;aHR0cHM6Ly93d3cubHVvZ3UuY29tLmNuL3Byb2JsZW0vUDE4MTY=&#34;&gt;例题：忠诚&lt;/span&gt;&lt;/p&gt;
&lt;p&gt;Code:&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;#include &amp;lt;cstdlib&amp;gt;
#include &amp;lt;cstdio&amp;gt;
#include &amp;lt;cmath&amp;gt;
#include &amp;lt;algorithm&amp;gt;
using namespace std;
const int N=100001;
int z[N][20];
int n,m;
int count1(int x)
&amp;#123;
    int need=1;
    for(int i=0;i&amp;lt;x;i++)
        need=need*2;
    return need;
&amp;#125;
void create(int n)
&amp;#123;
    for(int i=1;count1(i)&amp;lt;=n;i++)
    &amp;#123;
        for(int j=1;j+count1(i)-1&amp;lt;=n;j++)
            z[j][i]=min(z[j][i-1],z[j+(1&amp;lt;&amp;lt;(i-1))][i-1]);
    &amp;#125;
    return;
&amp;#125;
int work(int l,int r)
&amp;#123;
    int t=(int)(log((r-l+1)*1.0)/log(2.0));
    return min(z[l][t],z[r-(1&amp;lt;&amp;lt;t)+1][t]);
&amp;#125;
int main()
&amp;#123;
    scanf(&amp;quot;%d%d&amp;quot;,&amp;amp;m,&amp;amp;n);
    for(int i=1;i&amp;lt;=m;i++)
        scanf(&amp;quot;%d&amp;quot;,&amp;amp;z[i][0]);
    create(m);
    for(int i=1;i&amp;lt;=n;i++)
    &amp;#123;
        int a,b;
        scanf(&amp;quot; %d %d&amp;quot;,&amp;amp;a,&amp;amp;b);
        printf(&amp;quot;%d &amp;quot;,work(a,b));
    &amp;#125;
    return 0;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;具体算法原理请移步另一篇文章&lt;/p&gt;

 &lt;h1 id=&#34;线段树模板&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#线段树模板&#34;&gt;#&lt;/a&gt; 线段树模板&lt;/h1&gt;
&lt;p&gt;&lt;span id=&#34;more&#34;&gt;&lt;/span&gt;&lt;/p&gt;
&lt;h2 id=&#34;一-线段树基本概念&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#一-线段树基本概念&#34;&gt;#&lt;/a&gt; 一、线段树基本概念&lt;/h2&gt;
&lt;p&gt;1、线段树是一颗二叉树，储存的是一个区间的信息，每个节点都是一个结构体，结构体里面包含区间左端点，区间右端点，区间所需要维护的信息&lt;/p&gt;
&lt;p&gt;2、线段树基本思想是二分&lt;/p&gt;
&lt;p&gt;3、线段树一般结构如下图&lt;/p&gt;
&lt;p&gt;&lt;img data-src=&#34;https://s2.loli.net/2023/10/26/wQrvcMWS4x5tNAe.png&#34; alt=&#34;线段树基本结构&#34; /&gt;&lt;/p&gt;
&lt;p&gt;本人线段树一般书写方法不太一样，如下：&lt;/p&gt;
&lt;p&gt;Code:&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;struct node
&amp;#123;
    int l,r,left_child,right_child;
    int sum,delay;
&amp;#125;tree[N*2]; //线段树每一个节点结构体

int root,tot,d[N];

void push_up(root) //合并操作
&amp;#123;
    int a=tree[root].left_child,b=tree[root].right_child;
    tree[root].sum=tree[a].sum+tree[b].sum;
    tree[root].sum+=(tree[a].r-tree[a].l+1)*tree[a].delay,tree[root].sum+=(tree[b].r-tree[b].l+1)*tree[b].delay;
    return;
&amp;#125;

void build(int &amp;amp; root,int l,int r) //建树
&amp;#123;
    root=++tot,tree[root].l=l,tree[root].r=r;
    if(l==r)
    &amp;#123;
        tree[root].sum=d[l];
        return;
    &amp;#125;
    int mid=(l+r)&amp;gt;&amp;gt;1;
    build(tree[root].left_child,l,mid);
    build(tree[root].right_child,mid+1,r);
    push_up(root);
    return;
&amp;#125;

void push_down(int root) //修改时将延迟标记落实
&amp;#123;
    int a=tree[root].left_child,b=tree[root].right_child;
    if(tree[root].delay)
    &amp;#123;
        tree[a].sum+=(tree[a].r-tree[a].l+1)*tree[root].delay,tree[a].delay+=tree[root].delay;
        tree[b].sum+=(tree[b].r-tree[b].l+1)*tree[root].delay,tree[b].delay+=tree[root].delay;
        tree[root].delay=0;
    &amp;#125;
    return;
&amp;#125;

void update(int root,int l,int r,int v) //区间修改
&amp;#123;
    if(l&amp;lt;=tree[root].l &amp;amp;&amp;amp; tree[root].r&amp;lt;=r)
    &amp;#123;
        tree[root].delay+=v;
        return;
    &amp;#125;
    push_down(root);
    int mid=(tree[root].l+tree[root].r)&amp;gt;&amp;gt;1;
    if(mid&amp;gt;=r)
        update(tree[root].left_child,l,r,v);
    else if(mid&amp;lt;l)
        update(tree[root].right_child,l,r,v);
    else
        update(tree[root].left_child,l,mid,v),update(tree[root].right_child,mid+1,r,v);
    push_up(root);
    return;
&amp;#125;

int query(int root,int l,int r) //区间查询
&amp;#123;
    if(l&amp;lt;=tree[root].l &amp;amp;&amp;amp; tree[root].r&amp;lt;=r)
        return tree[root].sum;
    push_down(root);
    int mid=(tree[root].l+tree[root].r)&amp;gt;&amp;gt;1;
    if(mid&amp;gt;=r)
        return query(tree[root].left_child,l,r);
    else if(mid&amp;lt;l)
        return query(tree[root].right_child,l,r);
    else
        return query(tree[root].left_child,l,mid)+query(tree[root].right_child,mid+1,r);
&amp;#125;

void change(int root,int id,int v) //单点修改
&amp;#123;
    if(tree[root].l==tree[root].r)
    &amp;#123;
        tree[root].sum+=v;
        return;
    &amp;#125;
    int mid=(tree[root].l+tree[root].r)&amp;gt;&amp;gt;1;
    push_down(root);
    if(mid&amp;gt;=id)
        change(tree[root].left_child,l,r);
    else
        change(tree[root].right_child,l,r);
    push_up(root);
    return;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
&lt;h2 id=&#34;二-线段树板子包含区间求和单点修改区间修改&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#二-线段树板子包含区间求和单点修改区间修改&#34;&gt;#&lt;/a&gt; 二、线段树板子（包含区间求和，单点修改，区间修改）&lt;/h2&gt;
&lt;p&gt;&lt;span class=&#34;exturl&#34; data-url=&#34;aHR0cHM6Ly93d3cubHVvZ3UuY29tLmNuL3Byb2JsZW0vUDMzNzI=&#34;&gt;例题：线段树模板 1&lt;/span&gt;&lt;/p&gt;
&lt;p&gt;&lt;span class=&#34;exturl&#34; data-url=&#34;aHR0cHM6Ly93d3cubHVvZ3UuY29tLmNuL3Byb2JsZW0vUDMzNzM=&#34;&gt;例题：线段树模板 2&lt;/span&gt;&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;#include &amp;lt;bits/stdc++.h&amp;gt;
using namespace std;
const long long N=100001;
struct node
&amp;#123;
	long long l,r,left_child,right_child;
	long long sum,delay1,delay2;
&amp;#125;tree[N*2];
long long n,m,root,tot,d[N],mod;
void fix(long long root)
&amp;#123;
	long long a=tree[root].left_child,b=tree[root].right_child;
	tree[root].sum=tree[a].sum+tree[b].sum;
	tree[root].sum%=mod;
	return;
&amp;#125;
void build(long long &amp;amp; root,long long l,long long r)
&amp;#123;
	root=++tot,tree[root].l=l,tree[root].r=r;
	tree[root].delay2=1;
	if(l==r)
	&amp;#123;
		tree[root].sum=d[l];
		return;
	&amp;#125;
	long long mid=(l+r)/2;
	build(tree[root].left_child,l,mid);
	build(tree[root].right_child,mid+1,r);
	fix(root);
	return;
&amp;#125;
void push_down(long long root)
&amp;#123;
	long long a=tree[root].left_child,b=tree[root].right_child;
    tree[a].sum*=tree[root].delay2,tree[a].sum+=(tree[a].r-tree[a].l+1)*tree[root].delay1;
    tree[a].delay1=tree[a].delay1*tree[root].delay2+tree[root].delay1,tree[a].delay2*=tree[root].delay2;
    tree[a].delay1%=mod,tree[a].delay2%=mod,tree[a].sum%=mod;
    tree[b].sum*=tree[root].delay2,tree[b].sum+=(tree[b].r-tree[b].l+1)*tree[root].delay1;
    tree[b].delay1=tree[b].delay1*tree[root].delay2+tree[root].delay1,tree[b].delay2*=tree[root].delay2;
    tree[b].delay1%=mod,tree[b].delay2%=mod,tree[b].sum%=mod;
    tree[root].delay1=0,tree[root].delay2=1,tree[root].sum%=mod;
    return;
&amp;#125;
void update(long long root,long long l,long long r,long long v)
&amp;#123;
	if(l&amp;lt;=tree[root].l &amp;amp;&amp;amp; tree[root].r&amp;lt;=r)
	&amp;#123;
		tree[root].sum+=(tree[root].r-tree[root].l+1)*v;
		tree[root].delay1+=v;
		return;
	&amp;#125;
	push_down(root);
	long long mid=(tree[root].l+tree[root].r)/2;
	if(mid&amp;gt;=r)
		update(tree[root].left_child,l,r,v);
	else if(mid&amp;lt;l)
		update(tree[root].right_child,l,r,v);
	else
		update(tree[root].left_child,l,mid,v),update(tree[root].right_child,mid+1,r,v);
	fix(root);
	return;
&amp;#125;
void change(long long root,long long l,long long r,long long v)
&amp;#123;
	if(l&amp;lt;=tree[root].l &amp;amp;&amp;amp; tree[root].r&amp;lt;=r)
	&amp;#123;
		tree[root].sum*=v,tree[root].delay1*=v,tree[root].delay2*=v;
		tree[root].sum%=mod,tree[root].delay1%=mod,tree[root].delay2%=mod;
		return;
	&amp;#125;
	push_down(root);
	long long mid=(tree[root].l+tree[root].r)/2;
	if(mid&amp;gt;=r)
		change(tree[root].left_child,l,r,v);
	else if(mid&amp;lt;l)
		change(tree[root].right_child,l,r,v);
	else
		change(tree[root].left_child,l,mid,v),change(tree[root].right_child,mid+1,r,v);
	fix(root);
	return;
&amp;#125;
long long query(long long root,long long l,long long r)
&amp;#123;
	if(l&amp;lt;=tree[root].l &amp;amp;&amp;amp; tree[root].r&amp;lt;=r)
		return tree[root].sum%mod;
	long long mid=(tree[root].l+tree[root].r)/2;
	push_down(root);
	if(mid&amp;gt;=r)
		return query(tree[root].left_child,l,r)%mod;
	else if(mid&amp;lt;l)
		return query(tree[root].right_child,l,r)%mod;
	else
		return query(tree[root].left_child,l,mid)%mod+query(tree[root].right_child,mid+1,r)%mod;
&amp;#125;
int main()
&amp;#123;
	scanf(&amp;quot;%lld %lld %lld&amp;quot;,&amp;amp;n,&amp;amp;m,&amp;amp;mod);
	for(long long i=1;i&amp;lt;=n;i++)
		scanf(&amp;quot;%lld&amp;quot;,&amp;amp;d[i]);
	build(root,1,n);
	for(long long i=1;i&amp;lt;=m;i++)
	&amp;#123;
		long long op;
		scanf(&amp;quot;%lld&amp;quot;,&amp;amp;op);
		if(op==1)
		&amp;#123;
			long long l,r,x;
			scanf(&amp;quot;%lld %lld %lld&amp;quot;,&amp;amp;l,&amp;amp;r,&amp;amp;x);
			change(root,l,r,x);
		&amp;#125;
		else if(op==2)
		&amp;#123;
			long long l,r,x;
			scanf(&amp;quot;%lld %lld %lld&amp;quot;,&amp;amp;l,&amp;amp;r,&amp;amp;x);
			update(root,l,r,x);
		&amp;#125;
		else
		&amp;#123;
			long long l,r;
			scanf(&amp;quot;%lld %lld&amp;quot;,&amp;amp;l,&amp;amp;r);
			printf(&amp;quot;%lld\n&amp;quot;,query(root,l,r)%mod);
		&amp;#125;
	&amp;#125;
	return 0;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;线段树还可以进行其他操作，如区间最大、小值等功能，这些功能待补充&lt;/p&gt;
&lt;h2 id=&#34;三-可持久化线段树&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#三-可持久化线段树&#34;&gt;#&lt;/a&gt; 三、可持久化线段树&lt;/h2&gt;
&lt;p&gt;光看标题不明所以，其主要功能是可以求区间任意第 k 小的值，k 是可输入改变的&lt;/p&gt;
&lt;p&gt;&lt;span class=&#34;exturl&#34; data-url=&#34;aHR0cHM6Ly93d3cubHVvZ3UuY29tLmNuL3Byb2JsZW0vUDM4MzQ=&#34;&gt;例题：可持久化线段树&lt;/span&gt;&lt;/p&gt;
&lt;p&gt;Code:&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;#include &amp;lt;bits/stdc++.h&amp;gt;
using namespace std;
const int N=200001;
struct node
&amp;#123;
	int left,right,sum;
&amp;#125;tree[20*N];
struct edge
&amp;#123;
	int v,old,new_id;
	bool operator &amp;lt; (const edge &amp;amp; ot) const
	&amp;#123;
		return v&amp;lt;ot.v;
	&amp;#125;
&amp;#125;d[N];
int n,m,root[20*N],tot=1;
void update(int &amp;amp; now,int l,int r,int pos)
&amp;#123;
	tree[tot++]=tree[now];
	now=tot-1;
	tree[now].sum++;
	if(l==r)
		return;
	int mid=(l+r)/2;
	if(pos&amp;lt;=mid)
		update(tree[now].left,l,mid,pos);
	else
		update(tree[now].right,mid+1,r,pos);
	return;
&amp;#125;
int query(int last,int now,int l,int r,int pos)
&amp;#123;
	int sum=tree[tree[now].left].sum-tree[tree[last].left].sum;
	if(l==r)
		return l;
	int mid=(l+r)/2;
	if(pos&amp;lt;=sum)
		return query(tree[last].left,tree[now].left,l,mid,pos);
	else
		return query(tree[last].right,tree[now].right,mid+1,r,pos-sum);
&amp;#125;
int main()
&amp;#123;
	scanf(&amp;quot;%d %d&amp;quot;,&amp;amp;n,&amp;amp;m);
	for(int i=1;i&amp;lt;=n;i++)
		scanf(&amp;quot;%d&amp;quot;,&amp;amp;d[i].v),d[i].old=i;
	sort(d+1,d+1+n);
	for(int i=1;i&amp;lt;=n;i++)
		d[d[i].old].new_id=i;
	for(int i=1;i&amp;lt;=n;i++)
	&amp;#123;
		root[i]=root[i-1];
		update(root[i],1,n,d[i].new_id);
	&amp;#125;
	for(int i=1;i&amp;lt;=m;i++)
	&amp;#123;
		int l,r,k;
		scanf(&amp;quot;%d %d %d&amp;quot;,&amp;amp;l,&amp;amp;r,&amp;amp;k);
		printf(&amp;quot;%d\n&amp;quot;,d[query(root[l-1],root[r],1,n,k)].v);
	&amp;#125;
	return 0;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
&lt;h2 id=&#34;四-普通平衡树&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#四-普通平衡树&#34;&gt;#&lt;/a&gt; 四、普通平衡树&lt;/h2&gt;
&lt;p&gt;普通平衡树是一颗二叉树，特点是可以动态插入删除，也可以动态查询某个数的排名及其前驱和后继&lt;/p&gt;
&lt;p&gt;&lt;span class=&#34;exturl&#34; data-url=&#34;aHR0cHM6Ly93d3cubHVvZ3UuY29tLmNuL3Byb2JsZW0vUDMzNjk=&#34;&gt;例题：普通平衡树&lt;/span&gt;&lt;/p&gt;
&lt;p&gt;Code:&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;#include &amp;lt;bits/stdc++.h&amp;gt;
using namespace std;
const int N=100001;
int ch[N][2],par[N],size[N],cnt[N],val[N],n,tot,root;
bool flag[N];
int chk(int x) 
&amp;#123;
	if(ch[par[x]][1]==x)  
		return 1;
	else
		return 0;
&amp;#125;
void push_up(int x) 
&amp;#123;
	size[x]=size[ch[x][0]]+size[ch[x][1]]+cnt[x];
	return;
&amp;#125;
void rotate(int cur)
&amp;#123;
	int x=par[cur],y=par[par[cur]],k=chk(cur);
	int which=ch[cur][k^1]; 
	ch[x][k]=which,par[which]=x; 
	ch[y][chk(x)]=cur,par[cur]=y;
	ch[cur][k^1]=x,par[x]=cur;
	push_up(x),push_up(cur);
	return;
&amp;#125;
void splay(int cur,int aim=0)
&amp;#123;
	while(par[cur]!=aim)
	&amp;#123;
		int x=par[cur],y=par[par[cur]];
		if(y!=aim)
		&amp;#123;
			if(chk(cur)==chk(x))
				rotate(x);
			else
				rotate(cur);
		&amp;#125;
		rotate(cur);
	&amp;#125;
	if(!aim)
		root=cur;
	return;
&amp;#125;
void insert(int x)
&amp;#123;
	int cur=root,fa=0;
	while(cur &amp;amp;&amp;amp; val[cur]!=x)
	&amp;#123;
		fa=cur;
		if(x&amp;gt;val[cur]) 
			cur=ch[cur][1];  
		else
			cur=ch[cur][0];
	&amp;#125;
	if(cur)
		cnt[cur]++;
	else 
	&amp;#123;
		cur=++tot;
		if(fa) 
		&amp;#123;
			if(x&amp;gt;val[fa]) 
				ch[fa][1]=cur;
			else
				ch[fa][0]=cur;
		&amp;#125;
		ch[cur][0]=ch[cur][1]=0,par[cur]=fa,val[cur]=x,cnt[cur]=size[cur]=1; 
	&amp;#125;
	splay(cur); 
	return;
&amp;#125;
void pushdown(int x)
&amp;#123;
	if(flag[x])
	&amp;#123;
		swap(ch[x][0],ch[x][1]);
		flag[ch[x][0]]=!flag[ch[x][0]];
		flag[ch[x][1]]=!flag[ch[x][1]];
		flag[x]=false;
	&amp;#125;
	return;
&amp;#125;
int kth(int x)
&amp;#123;
	int cur=root;
	for(;;)
	&amp;#123;
		if(ch[cur][0] &amp;amp;&amp;amp; x&amp;lt;=size[ch[cur][0]])
			cur=ch[cur][0];
		else if(x&amp;gt;size[ch[cur][0]]+cnt[cur])
		&amp;#123;
			x-=size[ch[cur][0]]+cnt[cur];
			cur=ch[cur][1];
		&amp;#125;
		else
			return cur;
	&amp;#125;
	return cur;
&amp;#125;
void update(int x,int y)
&amp;#123;
	int t1=kth(x),t2=kth(y+2);
	splay(t1),splay(t2,t1);
	flag[ch[t2][0]]=!flag[ch[t2][0]];
	return;
&amp;#125;
void find(int x)
&amp;#123;
	int cur=root;
	while(ch[cur][x&amp;gt;val[cur]] &amp;amp;&amp;amp; val[cur]!=x)
		cur=ch[cur][x&amp;gt;val[cur]];
	splay(cur);
	return;
&amp;#125;
int pre(int x)
&amp;#123;
	find(x);
	if(val[root]&amp;lt;x)
		return root;
	int cur=ch[root][0];
	while(ch[cur][1])
		cur=ch[cur][1];
	return cur;
&amp;#125;
int succ(int x)
&amp;#123;
	find(x);
	if(val[root]&amp;gt;x)
		return root;
	int cur=ch[root][1];
	while(ch[cur][0])
		cur=ch[cur][0];
	return cur;
&amp;#125;
void print(int x)
&amp;#123;
	pushdown(x);
	if(ch[x][0])
		print(ch[x][0]);
	if(val[x] &amp;amp;&amp;amp; val[x]&amp;lt;=n)
		printf(&amp;quot;%d &amp;quot;,val[x]);
	if(ch[x][1])
		print(ch[x][1]);
	return;
&amp;#125;
void del(int x)
&amp;#123;
	int last=pre(x),next=succ(x);
	splay(last),splay(next,last);
	int cut=ch[next][0];
	if(cnt[cut]&amp;gt;1)
	&amp;#123;
		cnt[cut]--;
		splay(cut);
	&amp;#125;
	else
		ch[next][0]=0,push_up(next),push_up(root);
	return;
&amp;#125;
int main()
&amp;#123;
	scanf(&amp;quot;%d&amp;quot;,&amp;amp;n);
	insert(0x7f7f7f7f),insert(0xcfcfcfcf);
	while(n--)
	&amp;#123;
		int op,x;
		scanf(&amp;quot;%d %d&amp;quot;,&amp;amp;op,&amp;amp;x);
		if(op==1)
			insert(x);
		else if(op==2)
			del(x);
		else if(op==3)
		&amp;#123;
			find(x);
			printf(&amp;quot;%d\n&amp;quot;,size[ch[root][0]]);
		&amp;#125;
		else if(op==4)
			printf(&amp;quot;%d\n&amp;quot;,val[kth(x+1)]);
		else if(op==5)
			printf(&amp;quot;%d\n&amp;quot;,val[pre(x)]);
		else
			printf(&amp;quot;%d\n&amp;quot;,val[succ(x)]);
	&amp;#125;
	return 0;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;这篇文章只是单纯的模板记录文章，要想了解原理请移步另一篇文章&lt;/p&gt;
&lt;p&gt;下面是二叉平衡树的应用，利用二叉平衡树来维护一个有序序列&lt;/p&gt;
&lt;p&gt;&lt;span class=&#34;exturl&#34; data-url=&#34;aHR0cHM6Ly93d3cubHVvZ3UuY29tLmNuL3Byb2JsZW0vUDMzOTE=&#34;&gt;例题：文艺平衡树&lt;/span&gt;&lt;/p&gt;
&lt;p&gt;Code:&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;#include &amp;lt;bits/stdc++.h&amp;gt;
using namespace std;
const int N=100001;
int ch[N][2],par[N],size[N],cnt[N],val[N],n,m,tot,root;
bool flag[N];
int chk(int x)//查询子节点在父节点左边还是右边 
&amp;#123;
	if(ch[par[x]][1]==x)  
		return 1;
	else
		return 0;
&amp;#125;
void push_up(int x) //更新此节点的size 
&amp;#123;
	size[x]=size[ch[x][0]]+size[ch[x][1]]+cnt[x];
	return;
&amp;#125;
void rotate(int cur)
&amp;#123;
	int x=par[cur],y=par[par[cur]],k=chk(cur),which=ch[cur][k^1]; 
	//x为此节点的父亲，y为此节点的爷爷 
	ch[x][k]=which,par[which]=x; //父亲的儿子改成 
	ch[y][chk(x)]=cur,par[cur]=y;
	ch[cur][k^1]=x,par[x]=cur;
	push_up(x),push_up(cur);
	return;
&amp;#125;
void splay(int cur,int aim=0)
&amp;#123;
	while(par[cur]!=aim)
	&amp;#123;
		int x=par[cur],y=par[par[cur]];
		if(y!=aim)
		&amp;#123;
			if(chk(cur)==chk(x))
				rotate(x);
			else
				rotate(cur);
		&amp;#125;
		rotate(cur);
	&amp;#125;
	if(!aim)
		root=cur;
	return;
&amp;#125;
void insert(int x)
&amp;#123;
	int cur=root,fa=0;
	while(cur &amp;amp;&amp;amp; val[cur]!=x) //当树不为空且这个点的值不是插入值 
	&amp;#123;
		fa=cur;//更新father 
		if(x&amp;gt;val[cur]) //这个值大于当前节点值 
			cur=ch[cur][1]; //右儿子 
		else
			cur=ch[cur][0]; //左二子 
	&amp;#125;
	if(cur) //出循环还没到底部，说明已经有这个值 
		cnt[cur]++;
	else //新建节点 
	&amp;#123;
		cur=++tot;
		if(fa) //如果这个点不是第一个点 
		&amp;#123;
			if(x&amp;gt;val[fa]) //同上 
				ch[fa][1]=cur;
			else
				ch[fa][0]=cur;
		&amp;#125;
		ch[cur][0]=ch[cur][1]=0,par[cur]=fa,val[cur]=x,cnt[cur]=size[cur]=1;//新节点信息 
	&amp;#125;
	splay(cur); //平衡一下 
	return;
&amp;#125;
void pushdown(int x)
&amp;#123;
	if(flag[x])
	&amp;#123;
		swap(ch[x][0],ch[x][1]);
		flag[ch[x][0]]=!flag[ch[x][0]];
		flag[ch[x][1]]=!flag[ch[x][1]];
		flag[x]=false;
	&amp;#125;
	return;
&amp;#125;
int kth(int x)
&amp;#123;
	int cur=root;
	for(;;)
	&amp;#123;
		pushdown(cur);
		if(ch[cur][0] &amp;amp;&amp;amp; x&amp;lt;=size[ch[cur][0]])
			cur=ch[cur][0];
		else if(x&amp;gt;size[ch[cur][0]]+cnt[cur])
		&amp;#123;
			x-=size[ch[cur][0]]+cnt[cur];
			cur=ch[cur][1];
		&amp;#125;
		else
			return cur;
	&amp;#125;
	return cur;
&amp;#125;
void update(int x,int y)
&amp;#123;
	int t1=kth(x),t2=kth(y+2);
	splay(t1),splay(t2,t1);
	flag[ch[t2][0]]=!flag[ch[t2][0]];
	return;
&amp;#125;
void find(int x)
&amp;#123;
	int cur=root;
	while(ch[cur][x&amp;gt;val[cur]] &amp;amp;&amp;amp; val[cur]!=x)
		cur=ch[cur][x&amp;gt;val[cur]];
	splay(cur);
	return;
&amp;#125;
int pre(int x)
&amp;#123;
	find(x);
	if(val[root]&amp;gt;x)
		return root;
	int cur=ch[root][0];
	while(ch[cur][1])
		cur=ch[cur][1];
	return cur;
&amp;#125;
int succ(int x)
&amp;#123;
	find(x);
	if(val[root]&amp;lt;x)
		return root;
	int cur=ch[root][1];
	while(ch[cur][0])
		cur=ch[cur][1];
	return cur;
&amp;#125;
void print(int x)
&amp;#123;
	pushdown(x);
	if(ch[x][0])
		print(ch[x][0]);
	if(val[x] &amp;amp;&amp;amp; val[x]&amp;lt;=n)
		printf(&amp;quot;%d &amp;quot;,val[x]);
	if(ch[x][1])
		print(ch[x][1]);
	return;
&amp;#125;
void del(int x)
&amp;#123;
	int last=pre(x),next=succ(x);
	splay(last),splay(next,last);
	int cut=ch[next][0];
	if(cnt[cut]&amp;gt;1)
	&amp;#123;
		cnt[cut]--;
		splay(cut);
	&amp;#125;
	else
		ch[next][0]=0,push_up(next),push_up(root);
	return;
&amp;#125;
int main()
&amp;#123;
	scanf(&amp;quot;%d %d&amp;quot;,&amp;amp;n,&amp;amp;m);
	for(int i=0;i&amp;lt;=n+1;i++)
		insert(i);
	for(int i=1;i&amp;lt;=m;i++)
	&amp;#123;
		int x,y;
		scanf(&amp;quot;%d %d&amp;quot;,&amp;amp;x,&amp;amp;y);
		update(x,y);
	&amp;#125;
	print(root);
	return 0;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
&lt;h2 id=&#34;五-树链剖分&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#五-树链剖分&#34;&gt;#&lt;/a&gt; 五、树链剖分&lt;/h2&gt;
&lt;p&gt;树链剖分是为了对树上进行区间修改而发明的算法，很长，很难写，但应用很多&lt;/p&gt;
&lt;p&gt;&lt;span class=&#34;exturl&#34; data-url=&#34;aHR0cHM6Ly93d3cubHVvZ3UuY29tLmNuL3Byb2JsZW0vUDMzODQ=&#34;&gt;例题：重链剖分&lt;/span&gt;&lt;/p&gt;
&lt;p&gt;Code:&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;#include &amp;lt;cstdio&amp;gt;
#include &amp;lt;cstdlib&amp;gt;
#include &amp;lt;vector&amp;gt;
using namespace std;
const int N=100001;
struct data
&amp;#123;
    int h;
    int zson;
    int fa,top;
    int size;
    int tid;
&amp;#125;d[N];
struct node
&amp;#123;
    int l,r,left_child,right_child;
    int sum,delay;
&amp;#125;tree[N*3];
int rnk[N],n,m,R,mod,a[N],cnt=1,root,tot;
vector &amp;lt;int &amp;gt; v[N];
void dfs1(int pre,int cur)
&amp;#123;
    d[cur].h=d[pre].h+1;
    d[cur].fa=pre,d[cur].zson=-1;
    d[cur].size=1;
    int size=v[cur].size();
    for(int i=0;i&amp;lt;size;i++)
    &amp;#123;
        int t=v[cur][i];
        if(t!=pre)
        &amp;#123;
            dfs1(cur,t);
            d[cur].size+=d[t].size;
            if(d[cur].zson==-1 || d[t].size&amp;gt;d[d[cur].zson].size)
            d[cur].zson=t;
        &amp;#125;
    &amp;#125;
    return;
&amp;#125;
void dfs2(int cur,int t)
&amp;#123;
    d[cur].top=t;
    d[cur].tid=cnt;
    rnk[cnt]=cur;
    cnt++;
    if(d[cur].size==1 || d[cur].zson==-1)
    return;
    dfs2(d[cur].zson,t);
    int size=v[cur].size();
    for(int i=0;i&amp;lt;size;i++)
    &amp;#123;
        int x=v[cur][i];
        if(x!=d[cur].fa &amp;amp;&amp;amp; x!=d[cur].zson)
        dfs2(x,x);
    &amp;#125;
    return;
&amp;#125;
void MOD(int root)
&amp;#123;
    tree[root].sum%=mod,tree[root].delay%=mod;
    return;
&amp;#125;
void fix(int root)
&amp;#123;
    int a=tree[root].left_child,b=tree[root].right_child;
    if(a!=-1 &amp;amp;&amp;amp; b!=-1)
    tree[root].sum=tree[a].sum+tree[b].sum;
    MOD(root);
    return;
&amp;#125;
void build(int &amp;amp; root,int l,int r)
&amp;#123;
    root=tot++;
    tree[root].l=l,tree[root].r=r;
    tree[root].left_child=tree[root].right_child=-1;
    tree[root].delay=0;
    if(l==r)
    tree[root].sum=a[rnk[l]];
    else
    &amp;#123;
        int mid=(l+r)/2;
        build(tree[root].left_child,l,mid);
        build(tree[root].right_child,mid+1,r);
        fix(root);
    &amp;#125;
    return;
&amp;#125;
void push_down(int root)
&amp;#123;
    if(tree[root].delay!=0)
    &amp;#123;
        int a=tree[root].left_child,b=tree[root].right_child;
        if(a==-1 || b==-1)
        return;
        tree[a].sum+=(tree[a].r-tree[a].l+1)*tree[root].delay;
        tree[a].delay+=tree[root].delay,tree[b].delay+=tree[root].delay;
        tree[b].sum+=(tree[b].r-tree[b].l+1)*tree[root].delay;
        tree[root].delay=0;
        MOD(root);
    &amp;#125;
    return;
&amp;#125;
void update(int root,int l,int r,int v)
&amp;#123;
    if(l&amp;lt;=tree[root].l &amp;amp;&amp;amp; tree[root].r&amp;lt;=r)
    &amp;#123;
        tree[root].sum+=(tree[root].r-tree[root].l+1)*v;
        tree[root].delay+=v;
    &amp;#125;
    else
    &amp;#123;
        push_down(root);
        int mid=(tree[root].l+tree[root].r)/2;
        if(mid&amp;gt;=r)
        update(tree[root].left_child,l,r,v);
        else if(mid&amp;lt;l)
        update(tree[root].right_child,l,r,v);
        else
        &amp;#123;
            update(tree[root].left_child,l,mid,v);
            update(tree[root].right_child,mid+1,r,v);
        &amp;#125;
        fix(root);
    &amp;#125;
    return;
&amp;#125;
void update_zdl(int x,int y,int z)
&amp;#123;
    int a=d[x].top,b=d[y].top;
    while(a!=b)
    &amp;#123;
        if(d[a].h&amp;gt;d[b].h)
        &amp;#123;
            update(root,d[a].tid,d[x].tid,z);
            x=d[a].fa;
        &amp;#125;
        else
        &amp;#123;
            update(root,d[b].tid,d[y].tid,z);
            y=d[b].fa;
        &amp;#125;
        a=d[x].top,b=d[y].top;
    &amp;#125;
    if(x!=y)
    &amp;#123;
        if(d[x].tid&amp;lt;d[y].tid)
        update(root,d[x].tid,d[y].tid,z);
        else
        update(root,d[y].tid,d[x].tid,z);
    &amp;#125;
    else
    update(root,d[x].tid,d[y].tid,z);
    return;
&amp;#125;
int get_ans(int root,int l,int r)
&amp;#123;
    if(l&amp;lt;=tree[root].l &amp;amp;&amp;amp; tree[root].r&amp;lt;=r)
    return tree[root].sum;
    else
    &amp;#123;
        push_down(root);
        int mid=(tree[root].l+tree[root].r)/2;
        if(mid&amp;gt;=r)
        return get_ans(tree[root].left_child,l,r)%mod;
        else if(mid&amp;lt;l)
        return get_ans(tree[root].right_child,l,r)%mod;
        return (get_ans(tree[root].left_child,l,mid)+get_ans(tree[root].right_child,mid+1,r))%mod;
    &amp;#125;
&amp;#125;
int get_ans_zdl(int x,int y)
&amp;#123;
    int ans=0;
    int a=d[x].top,b=d[y].top;
    while(a!=b)
    &amp;#123;
        if(d[a].h&amp;gt;d[b].h)
        &amp;#123;
            ans+=get_ans(root,d[a].tid,d[x].tid);
            x=d[a].fa;
        &amp;#125;
        else
        &amp;#123;
            ans+=get_ans(root,d[b].tid,d[y].tid);
            y=d[b].fa;
        &amp;#125;
        a=d[x].top,b=d[y].top;
    &amp;#125;
    if(x!=y)
    &amp;#123;
        if(d[x].tid&amp;lt;d[y].tid)
        ans+=get_ans(root,d[x].tid,d[y].tid);
        else
        ans+=get_ans(root,d[y].tid,d[x].tid);
    &amp;#125;
    else
    ans+=get_ans(root,d[x].tid,d[y].tid);
    return ans%mod;
&amp;#125;
int main()
&amp;#123;
    scanf(&amp;quot;%d %d %d %d&amp;quot;,&amp;amp;n,&amp;amp;m,&amp;amp;R,&amp;amp;mod);
    for(int i=1;i&amp;lt;=n;i++)
    scanf(&amp;quot;%d&amp;quot;,&amp;amp;a[i]);
    for(int i=1;i&amp;lt;n;i++)
    &amp;#123;
        int a,b;
        scanf(&amp;quot;%d %d&amp;quot;,&amp;amp;a,&amp;amp;b);
        v[a].push_back(b),v[b].push_back(a);
    &amp;#125;
    d[0].h=0,d[0].fa=-1;
    dfs1(0,R);
    dfs2(R,R);
    build(root,1,n);
    for(int i=1;i&amp;lt;=m;i++)
    &amp;#123;
        int op,x,y,z;
        scanf(&amp;quot;%d&amp;quot;,&amp;amp;op);
        if(op==1)
        &amp;#123;
            scanf(&amp;quot;%d %d %d&amp;quot;,&amp;amp;x,&amp;amp;y,&amp;amp;z);
            update_zdl(x,y,z);
        &amp;#125;
        else if(op==2)
        &amp;#123;
            scanf(&amp;quot;%d %d&amp;quot;,&amp;amp;x,&amp;amp;y);
            printf(&amp;quot;%d\n&amp;quot;,get_ans_zdl(x,y));
        &amp;#125;
        else if(op==3)
        &amp;#123;
            scanf(&amp;quot;%d %d&amp;quot;,&amp;amp;x,&amp;amp;z);
            update(root,d[x].tid,d[x].tid+d[x].size-1,z);
        &amp;#125;
        else
        &amp;#123;
            scanf(&amp;quot;%d&amp;quot;,&amp;amp;x);
            printf(&amp;quot;%d\n&amp;quot;,get_ans(root,d[x].tid,d[x].tid+d[x].size-1));
        &amp;#125;
    &amp;#125;
    return 0;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;和二叉平衡树一样，本篇只做记录，不做详解，详解请移步另一篇文章&lt;/p&gt;

 &lt;h1 id=&#34;倍增和lca的模板&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#倍增和lca的模板&#34;&gt;#&lt;/a&gt; 倍增和 LCA 的模板&lt;/h1&gt;
&lt;p&gt;&lt;span id=&#34;more&#34;&gt;&lt;/span&gt;&lt;/p&gt;
&lt;h2 id=&#34;一-倍增&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#一-倍增&#34;&gt;#&lt;/a&gt; 一、倍增&lt;/h2&gt;
&lt;p&gt;倍增及成倍增长，其时间复杂度为&lt;span class=&#34;katex&#34;&gt;&lt;span class=&#34;katex-mathml&#34;&gt;&lt;math xmlns=&#34;http://www.w3.org/1998/Math/MathML&#34;&gt;&lt;semantics&gt;&lt;mrow&gt;&lt;mi&gt;O&lt;/mi&gt;&lt;mo stretchy=&#34;false&#34;&gt;(&lt;/mo&gt;&lt;mi&gt;n&lt;/mi&gt;&lt;mi&gt;l&lt;/mi&gt;&lt;mi&gt;o&lt;/mi&gt;&lt;mi&gt;g&lt;/mi&gt;&lt;mi&gt;n&lt;/mi&gt;&lt;mo stretchy=&#34;false&#34;&gt;)&lt;/mo&gt;&lt;/mrow&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;O(nlogn)&lt;/annotation&gt;&lt;/semantics&gt;&lt;/math&gt;&lt;/span&gt;&lt;span class=&#34;katex-html&#34; aria-hidden=&#34;true&#34;&gt;&lt;span class=&#34;base&#34;&gt;&lt;span class=&#34;strut&#34; style=&#34;height:1em;vertical-align:-0.25em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.02778em;&#34;&gt;O&lt;/span&gt;&lt;span class=&#34;mopen&#34;&gt;(&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;n&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.01968em;&#34;&gt;l&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;o&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.03588em;&#34;&gt;g&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;n&lt;/span&gt;&lt;span class=&#34;mclose&#34;&gt;)&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;，初始化为&lt;span class=&#34;katex&#34;&gt;&lt;span class=&#34;katex-mathml&#34;&gt;&lt;math xmlns=&#34;http://www.w3.org/1998/Math/MathML&#34;&gt;&lt;semantics&gt;&lt;mrow&gt;&lt;mi&gt;O&lt;/mi&gt;&lt;mo stretchy=&#34;false&#34;&gt;(&lt;/mo&gt;&lt;mi&gt;n&lt;/mi&gt;&lt;mi&gt;l&lt;/mi&gt;&lt;mi&gt;o&lt;/mi&gt;&lt;mi&gt;g&lt;/mi&gt;&lt;mi&gt;n&lt;/mi&gt;&lt;mo stretchy=&#34;false&#34;&gt;)&lt;/mo&gt;&lt;/mrow&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;O(nlogn)&lt;/annotation&gt;&lt;/semantics&gt;&lt;/math&gt;&lt;/span&gt;&lt;span class=&#34;katex-html&#34; aria-hidden=&#34;true&#34;&gt;&lt;span class=&#34;base&#34;&gt;&lt;span class=&#34;strut&#34; style=&#34;height:1em;vertical-align:-0.25em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.02778em;&#34;&gt;O&lt;/span&gt;&lt;span class=&#34;mopen&#34;&gt;(&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;n&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.01968em;&#34;&gt;l&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;o&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.03588em;&#34;&gt;g&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;n&lt;/span&gt;&lt;span class=&#34;mclose&#34;&gt;)&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;，查询为&lt;span class=&#34;katex&#34;&gt;&lt;span class=&#34;katex-mathml&#34;&gt;&lt;math xmlns=&#34;http://www.w3.org/1998/Math/MathML&#34;&gt;&lt;semantics&gt;&lt;mrow&gt;&lt;mi&gt;O&lt;/mi&gt;&lt;mo stretchy=&#34;false&#34;&gt;(&lt;/mo&gt;&lt;mn&gt;1&lt;/mn&gt;&lt;mo stretchy=&#34;false&#34;&gt;)&lt;/mo&gt;&lt;/mrow&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;O(1)&lt;/annotation&gt;&lt;/semantics&gt;&lt;/math&gt;&lt;/span&gt;&lt;span class=&#34;katex-html&#34; aria-hidden=&#34;true&#34;&gt;&lt;span class=&#34;base&#34;&gt;&lt;span class=&#34;strut&#34; style=&#34;height:1em;vertical-align:-0.25em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.02778em;&#34;&gt;O&lt;/span&gt;&lt;span class=&#34;mopen&#34;&gt;(&lt;/span&gt;&lt;span class=&#34;mord&#34;&gt;1&lt;/span&gt;&lt;span class=&#34;mclose&#34;&gt;)&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;
&lt;p&gt;至于倍增例题，没有特别好的只用倍增的例题，大部分倍增都是直接有应用了，最多的还是&lt;span class=&#34;katex&#34;&gt;&lt;span class=&#34;katex-mathml&#34;&gt;&lt;math xmlns=&#34;http://www.w3.org/1998/Math/MathML&#34;&gt;&lt;semantics&gt;&lt;mrow&gt;&lt;mi&gt;L&lt;/mi&gt;&lt;mi&gt;C&lt;/mi&gt;&lt;mi&gt;A&lt;/mi&gt;&lt;/mrow&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;LCA&lt;/annotation&gt;&lt;/semantics&gt;&lt;/math&gt;&lt;/span&gt;&lt;span class=&#34;katex-html&#34; aria-hidden=&#34;true&#34;&gt;&lt;span class=&#34;base&#34;&gt;&lt;span class=&#34;strut&#34; style=&#34;height:0.68333em;vertical-align:0em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;L&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.07153em;&#34;&gt;C&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;A&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;，具体代码可以在下面&lt;span class=&#34;katex&#34;&gt;&lt;span class=&#34;katex-mathml&#34;&gt;&lt;math xmlns=&#34;http://www.w3.org/1998/Math/MathML&#34;&gt;&lt;semantics&gt;&lt;mrow&gt;&lt;mi&gt;L&lt;/mi&gt;&lt;mi&gt;C&lt;/mi&gt;&lt;mi&gt;A&lt;/mi&gt;&lt;/mrow&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;LCA&lt;/annotation&gt;&lt;/semantics&gt;&lt;/math&gt;&lt;/span&gt;&lt;span class=&#34;katex-html&#34; aria-hidden=&#34;true&#34;&gt;&lt;span class=&#34;base&#34;&gt;&lt;span class=&#34;strut&#34; style=&#34;height:0.68333em;vertical-align:0em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;L&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.07153em;&#34;&gt;C&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;A&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; 的部分查看&lt;/p&gt;
&lt;h2 id=&#34;二-lca即最近公共祖先&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#二-lca即最近公共祖先&#34;&gt;#&lt;/a&gt; 二、LCA（即最近公共祖先）&lt;/h2&gt;
&lt;p&gt;最基础的&lt;span class=&#34;katex&#34;&gt;&lt;span class=&#34;katex-mathml&#34;&gt;&lt;math xmlns=&#34;http://www.w3.org/1998/Math/MathML&#34;&gt;&lt;semantics&gt;&lt;mrow&gt;&lt;mi&gt;L&lt;/mi&gt;&lt;mi&gt;C&lt;/mi&gt;&lt;mi&gt;A&lt;/mi&gt;&lt;/mrow&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;LCA&lt;/annotation&gt;&lt;/semantics&gt;&lt;/math&gt;&lt;/span&gt;&lt;span class=&#34;katex-html&#34; aria-hidden=&#34;true&#34;&gt;&lt;span class=&#34;base&#34;&gt;&lt;span class=&#34;strut&#34; style=&#34;height:0.68333em;vertical-align:0em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;L&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.07153em;&#34;&gt;C&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;A&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; 基本上可以说是倍增算法的应用（当然如果你是究极大佬直接用树剖当我没说）,&lt;span class=&#34;katex&#34;&gt;&lt;span class=&#34;katex-mathml&#34;&gt;&lt;math xmlns=&#34;http://www.w3.org/1998/Math/MathML&#34;&gt;&lt;semantics&gt;&lt;mrow&gt;&lt;mi&gt;L&lt;/mi&gt;&lt;mi&gt;C&lt;/mi&gt;&lt;mi&gt;A&lt;/mi&gt;&lt;/mrow&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;LCA&lt;/annotation&gt;&lt;/semantics&gt;&lt;/math&gt;&lt;/span&gt;&lt;span class=&#34;katex-html&#34; aria-hidden=&#34;true&#34;&gt;&lt;span class=&#34;base&#34;&gt;&lt;span class=&#34;strut&#34; style=&#34;height:0.68333em;vertical-align:0em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;L&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.07153em;&#34;&gt;C&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;A&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; 每次查询为&lt;span class=&#34;katex&#34;&gt;&lt;span class=&#34;katex-mathml&#34;&gt;&lt;math xmlns=&#34;http://www.w3.org/1998/Math/MathML&#34;&gt;&lt;semantics&gt;&lt;mrow&gt;&lt;mi&gt;l&lt;/mi&gt;&lt;mi&gt;o&lt;/mi&gt;&lt;mi&gt;g&lt;/mi&gt;&lt;mi&gt;n&lt;/mi&gt;&lt;/mrow&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;logn&lt;/annotation&gt;&lt;/semantics&gt;&lt;/math&gt;&lt;/span&gt;&lt;span class=&#34;katex-html&#34; aria-hidden=&#34;true&#34;&gt;&lt;span class=&#34;base&#34;&gt;&lt;span class=&#34;strut&#34; style=&#34;height:0.8888799999999999em;vertical-align:-0.19444em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.01968em;&#34;&gt;l&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;o&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.03588em;&#34;&gt;g&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;，整体时间复杂度还是倍增的时间复杂度，为&lt;span class=&#34;katex&#34;&gt;&lt;span class=&#34;katex-mathml&#34;&gt;&lt;math xmlns=&#34;http://www.w3.org/1998/Math/MathML&#34;&gt;&lt;semantics&gt;&lt;mrow&gt;&lt;mi&gt;O&lt;/mi&gt;&lt;mo stretchy=&#34;false&#34;&gt;(&lt;/mo&gt;&lt;mi&gt;n&lt;/mi&gt;&lt;mi&gt;l&lt;/mi&gt;&lt;mi&gt;o&lt;/mi&gt;&lt;mi&gt;g&lt;/mi&gt;&lt;mi&gt;n&lt;/mi&gt;&lt;mo stretchy=&#34;false&#34;&gt;)&lt;/mo&gt;&lt;/mrow&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;O(nlogn)&lt;/annotation&gt;&lt;/semantics&gt;&lt;/math&gt;&lt;/span&gt;&lt;span class=&#34;katex-html&#34; aria-hidden=&#34;true&#34;&gt;&lt;span class=&#34;base&#34;&gt;&lt;span class=&#34;strut&#34; style=&#34;height:1em;vertical-align:-0.25em;&#34;&gt;&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.02778em;&#34;&gt;O&lt;/span&gt;&lt;span class=&#34;mopen&#34;&gt;(&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;n&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.01968em;&#34;&gt;l&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;o&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34; style=&#34;margin-right:0.03588em;&#34;&gt;g&lt;/span&gt;&lt;span class=&#34;mord mathnormal&#34;&gt;n&lt;/span&gt;&lt;span class=&#34;mclose&#34;&gt;)&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;
&lt;p&gt;&lt;span class=&#34;exturl&#34; data-url=&#34;aHR0cHM6Ly93d3cubHVvZ3UuY29tLmNuL3Byb2JsZW0vUDMzNzk=&#34;&gt;例题：最近公共祖先&lt;/span&gt;&lt;/p&gt;
&lt;p&gt;Code:&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;#include &amp;lt;cstdio&amp;gt;
#include &amp;lt;cstdlib&amp;gt;
#include &amp;lt;vector&amp;gt;
#include &amp;lt;algorithm&amp;gt;
#include &amp;lt;cstring&amp;gt;
using namespace std;
const int N=500001;
int fa[N][21];
int in[N];
int h[N];
int n,m,s,ans[20001][20001];
vector &amp;lt;int &amp;gt; son[N];
void dfs(int pre,int cur)
&amp;#123;
    fa[cur][0]=pre;
    h[cur]=h[pre]+1;
    for(int i=1;i&amp;lt;20;i++)
    	fa[cur][i]=fa[fa[cur][i-1]][i-1];
    for(int i=0;i&amp;lt;son[cur].size();i++)
    	if(son[cur][i]!=pre)
    		dfs(cur,son[cur][i]);
    return;
&amp;#125;
int lca(int a,int b)
&amp;#123;
    if(h[a]&amp;lt;h[b])
    	swap(a,b);
    for(int i=19;i&amp;gt;=0;i--)
    &amp;#123;
        if(h[a]-(1&amp;lt;&amp;lt;i)&amp;gt;=h[b])
        	a=fa[a][i];
    &amp;#125;
    if(a==b)
    	return a;
    for(int i=19;i&amp;gt;=0;i--)
    &amp;#123;
        if(fa[a][i]!=fa[b][i])
        	a=fa[a][i],b=fa[b][i];
    &amp;#125;
    return fa[a][0];
&amp;#125;
int main()
&amp;#123;
    scanf(&amp;quot;%d %d %d&amp;quot;,&amp;amp;n,&amp;amp;m,&amp;amp;s);
    for(int i=1;i&amp;lt;=n-1;i++)
    &amp;#123;
            int a,b;
            scanf(&amp;quot;%d %d&amp;quot;,&amp;amp;a,&amp;amp;b);
            son[a].push_back(b);
            son[b].push_back(a);
    &amp;#125;
    int root=s;
    dfs(0,root);
    for(int i=1;i&amp;lt;=m;i++)
    &amp;#123;
	    int q1,q2;
	    scanf(&amp;quot;%d %d&amp;quot;,&amp;amp;q1,&amp;amp;q2);
	    if(q1&amp;gt;20000 || q2&amp;gt;20000)
		&amp;#123;
			printf(&amp;quot;%d\n&amp;quot;,lca(q1,q2));
			continue; 
		&amp;#125;
	    if(ans[q1][q2]!=0)
	    	printf(&amp;quot;%d\n&amp;quot;,ans[q1][q2]);
	    else
	    &amp;#123;
	    	ans[q1][q2]=ans[q2][q1]=lca(q1,q2);
	    	printf(&amp;quot;%d\n&amp;quot;,ans[q1][q2]);
		&amp;#125;
	&amp;#125;
    return 0;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
